INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `MaleText`, `FemaleText`, `VerifiedBuild`) VALUES (100002, 'deDE', 'Willkommen, Reisender. Du suchst das Gefühl der Geschwindigkeit und den Wind im Fell, nicht wahr?$B$BIch, Edgar Nachtstreif, lehre nur die, die den Mut und den Willen haben, den Weg des Reiters zu gehen.$B$BNun, bist du bereit, die Zügel zu ergreifen und deine Reise zu beschleunigen? Lass uns keine Zeit verschwenden – die Nacht wartet nicht.', 'Willkommen, Reisender. Du suchst das Gefühl der Geschwindigkeit und den Wind im Fell, nicht wahr?$B$BIch, Edgar Nachtstreif, lehre nur die, die den Mut und den Willen haben, den Weg des Reiters zu gehen.$B$BNun, bist du bereit, die Zügel zu ergreifen und deine Reise zu beschleunigen? Lass uns keine Zeit verschwenden – die Nacht wartet nicht.', 16000);
