INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (17494, 'deDE', 'Ich wünschte, ich könnte dir ein Angebot machen, Partner – wirklich – aber zuerst musst du beim Bilgewasser-Kartell auf einem ehrfürchtigen Ruf sein.', 'Ich wünschte, ich könnte dir ein Angebot machen, Partner – wirklich – aber zuerst musst du beim Bilgewasser-Kartell auf einem ehrfürchtigen Ruf sein.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
