INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (17495, 'deDE', 'Yo! Ich bin Kall Goldkettchen, von den Bilgewasser-Trikes.$B$BSieh dich ruhig um. Such dir das Dreirad aus, das du WIRKLICH willst!$B$BEin Trike, das ein Vergnügen ist zu fahren... ein Trike, das gefährlich und todesmutig ist... ein Trike, auf das du STOLZ sein kannst!$B$BLass es mich wissen, wenn du es siehst, und wir machen einen Deal.', 'Yo! Ich bin Kall Goldkettchen, von den Bilgewasser-Trikes.$B$BSieh dich ruhig um. Such dir das Dreirad aus, das du WIRKLICH willst!$B$BEin Trike, das ein Vergnügen ist zu fahren... ein Trike, das gefährlich und todesmutig ist... ein Trike, auf das du STOLZ sein kannst!$B$BLass es mich wissen, wenn du es siehst, und wir machen einen Deal.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
