INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (17495, 'zhCN', 'Yo! I\'m Kall Worthaton, Bilgewater Trikes.$B$BGo ahead, look over the lot. Pick out the trike that you REALLY want!$B$BA trike that\'s a blast to ride... a trike that\'s dangerous and death-defying... a trike you can be PROUD of!$B$BYou let me know when you see it, and we\'ll make a deal.', '嗨!我是考尔·沃瑟顿，污水机动车。$B$B别客气，到处看看吧。选出你真正想要的机动车!$B$B一辆骑起来很爽的机动车...一辆危险而在生死边缘游转的机动车...一辆能让你感到骄傲的机动车!$B$B等你决定了，跟我说一声，我们可以马上成交。', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
DELETE FROM `npc_text_locale` WHERE `ID`=100000 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (100000, 'deDE', 'Ich kann dir beibringen, wie man selbst die schwierigsten Trikes fährt, vorausgesetzt, du hast das Kleingeld dafür!', 'Ich kann dir beibringen, wie man selbst die schwierigsten Trikes fährt, vorausgesetzt, du hast das Kleingeld dafür!', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
