INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500000, 'deDE', '', 'Eine schöne Stadt, nicht wahr? So wie Gilneas es einst war. Aber solche Dinge vergehen, ebenso wie wir. Nur die Erde und ihre Geheimnisse werden bleiben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500001 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500001, 'deDE', '', 'Die Worgen werden vergehen. Wir werden vergehen. Nur die Erde und ihre Geheimnisse werden bleiben. Bist du bereit zu lernen $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500002 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500002, 'deDE', '', 'Eine schöne Stadt, nicht wahr? So wie Gilneas es einst war. Aber solche Dinge vergehen, ebenso wie wir. Nur die Erde und ihre Geheimnisse werden bleiben.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500003 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500003, 'deDE', '', 'Die Worgen werden vergehen. Wir werden vergehen. Nur die Erde und ihre Geheimnisse werden bleiben. Bist du bereit zu lernen $C?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500004 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500004, 'deDE', '', 'Ah, ein Gleichgesinnter! Die Dunkelheit flüstert dir Geheimnisse zu, nicht wahr? Lass mich dir zeigen, wie du ihre Macht entfesselst... gegen die, die sie verdienen. Oder gegen jeden anderen, wie du willst.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500005 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500005, 'deDE', '', 'Pah! Du bist keine Hexenmeister, was willst du von mir? Geh zurück zu deinen kleinen Spielereien. Oder... willst du die wahre Macht kennenlernen? Nein? Dachte ich mir schon.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500006 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500006, 'deDE', '', 'Endlich ein echter Kämpfer! Sieh dir diese Muskeln an – oder zumindest das, was mal Muskeln werden könnten. Lass mich dir zeigen, wie man einen Gegner zu Brei haut, Goblin-Style!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500007 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500007, 'deDE', '', 'Ha! Du siehst nicht wie jemand aus, der \'ne ordentliche Keilerei überlebt. Warum bist du hier? Brauchst du \'nen Helm, der auch albern aussieht? Oder willst du lernen, wie echte Goblins kämpfen?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500008 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500008, 'deDE', '', 'Ah, ein Jäger wie ich! Es gibt nichts Schöneres, als einen perfekten Schuss abzugeben... außer vielleicht die Explosion, wenn du das Ziel verfehlst! Lass uns an deinem Ziel und deinen Fallen arbeiten, bevor dir jemand anderes den Spaß klaut.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500009 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500009, 'deDE', '', 'Hah! Du hast weder den Blick noch die Geduld eines Jägers. Was willst du? Einen Bogen, um dekorativ in der Ecke zu stehen? Oder vielleicht ein Haustier, das dich nicht ernst nimmt?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500010 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500010, 'deDE', '', 'Ah, willkommen, du Auserwählter des Lichts... oder der Schatten, je nachdem, wie du drauf bist. Egal, ob du heilst oder vernichtest, ich bringe dir bei, wie man’s macht – schnell, effizient und mit einem gewissen Goblin-Flair!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500011 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500011, 'deDE', '', 'Oh, sieh mal einer an – jemand, der nicht vom Licht berührt wurde. Was willst du von mir? Heilung? Die kostet extra. Oder suchst du Beistand für dein nächstes großes Abenteuer? Tipp: Das Licht hilft nicht jedem.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500012 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500012, 'deDE', '', 'Ah, ein Meister der Schatten – oder zumindest jemand, der gerne so tut! Lass mich dir zeigen, wie man richtig stiehlt, meuchelt und verschwindet, bevor jemand \'Wache!\' schreien kann. Goblin-Style natürlich.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500013 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500013, 'deDE', '', 'Pah! Du bist so subtil wie ein Hammerschlag auf den Daumen. Was willst du von einem Schurken wie mir? Willst du lernen, wie man im Schatten verschwindet, oder brauchst du einfach \'nen schnellen Dolch... mit Rabatt?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500014 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500014, 'deDE', '', 'Ah, ein Elementeflüsterer! Weißt du, das ist wie mit Maschinen – man muss nur wissen, welches Knöpfchen man drückt, und schon machen sie, was man will. Lass mich dir zeigen, wie man Wind, Erde, Feuer und Wasser wie einen gut geölten Apparat nutzt!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500015 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500015, 'deDE', '', 'Ha! Die Elemente arbeiten nur für Leute mit der richtigen Einstellung... oder für die, die wissen, wie man sie austrickst. Was willst du? Einen Blitz auf deinen Kopf oder ein paar Tipps für die nächste Regenwolke?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500016 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500016, 'deDE', '', 'Ah, jemand, der die edle Kunst des Schneiderns erlernen will! Stoffe zuschneiden, Fäden verweben und Nähte verstärken – ich bringe dir bei, wie du Kleidung herstellst, die alles aushält. Keine halben Sachen, nur echte Goblin-Qualität', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500017 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500017, 'deDE', '', 'Na, suchst du nach dem großen Goldfund? Oder willst du einfach lernen, wie man ordentlich Felsen kloppt? Egal, ob du Erze schürfen oder Steine zerbröseln willst – ich zeig dir, wie’s richtig geht. Und vergiss nicht: Ein Goblin geht niemals ohne Spitzhacke!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500018 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500018, 'deDE', '', 'Hunger ist der Feind, und gutes Essen ist die Waffe! Ich bringe dir bei, wie man mit ein bisschen Goblin-Kreativität aus fast allem ein Festmahl zaubert. Keine Sorge, die Explosionen sind meistens beabsichtigt... meistens.', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500019 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500019, 'deDE', '', 'Ah, ein Geschäftsenthusiast! Ich sehe es an dem Funkeln in deinen Augen... oder ist das Verzweiflung? Wie auch immer, du bist hier genau richtig! Beim Bilgewasserkartell verwandeln wir Träume in Gold und Gold in Träume – na ja, meistens meine Träume. Hast du eine Idee? Einen Plan? Vielleicht eine Ladung von etwas... \'inoffiziellem\'? Solange es klimpert oder glitzert, höre ich zu. Aber denk daran, Freund: Zeit ist Geld, und meine Zeit ist ein Vermögen wert. Also mach es schnell oder mach es profitabel!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500020 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500020, 'deDE', '', 'Es tut mir leid, aber Lorna wäre wirklich wütend, wenn ich dir ein Pferd geben würde. Sie sind nur für diejenigen, die bei Gilneas ehrfürchtig sind!', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500021 AND `Locale`='deDE';
INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500021, 'deDE', '', 'Lorna sagte, ein wahrhaft mutiger Abenteurer half dabei, viele der Pferde in der Nähe des Obstgartens ihrer Familie zu retten. Ich bot an, mich um diejenigen zu kümmern, die mit uns über das Meer gekommen sind. Das habe ich von meinen Eltern gelernt, der Licht segne sie! Interessierst du dich für ein feines gilnearisches Ross...?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
DELETE FROM `npc_text_locale` WHERE `ID`=500022 AND `Locale`='deDE';
