INSERT INTO `npc_text_locale` (`ID`, `Locale`, `Text0_0`, `Text0_1`, `Text1_0`, `Text1_1`, `Text2_0`, `Text2_1`, `Text3_0`, `Text3_1`, `Text4_0`, `Text4_1`, `Text5_0`, `Text5_1`, `Text6_0`, `Text6_1`, `Text7_0`, `Text7_1`) VALUES (500024, 'deDE', '', 'Ah, Mulgore! Friedlich, sonnig und voller Möglichkeiten für... Profit! Harpyien horten glänzende Schmuckstücke, die Tauren haben seltene Waren, und selbst die Stacheleber besitzen Dinge, die man plündern könnte. Ein Goblin wie ich blüht an einem Ort wie diesem auf! Noch etwas?', '', '', '', '', '', '', '', '', '', '', '', '', '', '');
