INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50055, 2, 0, 0, 0, 0, 0, 0, 0, 'Hmm... they blame us for destroying their home, ye say?  Well, that wasn\'t us, that was the horrible earthquake we had earlier today.  Are they that dense that they can\'t see we nearly lost our camp too?$B$BCan\'t blame a trogg for fightin\' for his home, though.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50056;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50056, 2, 0, 0, 0, 0, 0, 0, 0, 'I figured they weren’t here for the view. This is more serious than I feared! This isn’t just a raid. The Dark Iron dwarves aim to cripple Ironforge’s defensive strongholds! While we’re fighting here, they’ve attacked Ironforge’s airfield. We must act swiftly before they cause even more damage!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50057;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50057, 2, 0, 0, 0, 0, 0, 0, 0, 'Thank goodness you\'re here, $N. The airfield\'s been attacked by the Dark Irons and I don\'t think we can hold out much longer.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50058;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50058, 2, 0, 0, 0, 0, 0, 0, 0, 'Well done! My men are working hard to make our flying machines and steam tanks battle-ready.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50059;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50059, 2, 0, 0, 0, 0, 0, 0, 0, 'The soldiers discover hidden reserves of strength and courage and confront the Dark Iron Dwarves. As long as we are here, the airfield will not fall!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50060;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50060, 2, 0, 0, 0, 0, 0, 0, 0, 'The tide is turning, $N, largely thanks to your efforts. Now we must strike a blow against the leaders of the Dark Iron Dwarves.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50061;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50061, 2, 0, 0, 0, 0, 0, 0, 0, 'Success! Without your help, the enemy would have captured Ironforge Airfield. Then the Dark Iron Dwarves would have had free rein to march on Ironforge.$B$BThe Council of the Three Hammers must learn what has happened here.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50062;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50062, 2, 0, 0, 0, 0, 0, 0, 0, 'Your report on the attacks by the Dark Iron Dwarves is concerning, but your bravery has spared Ironforge a severe blow. Well done, $N. We will strengthen the defenses and ensure that we are prepared for any threat. You have served our cause loyally!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50063;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50063, 2, 0, 0, 0, 0, 0, 0, 0, 'Ah, good that you are here, $C. Commander Stonebreaker sent you? Excellent. There is much to do, and no time to lose. With your help, we can hold this airfield and show the Dark Iron Dwarves that we are not so easily defeated!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50064;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50064, 2, 0, 0, 0, 0, 0, 0, 0, 'Remarkable! I knew you were a true champion of Ironforge, $N. The defeat of Frostbane ensures the safety of our soldiers and the airfield alike. This victory will be sung about in taverns from Dun Morogh to Stormwind. Take this as a token of our gratitude.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50065;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50065, 2, 0, 0, 0, 0, 0, 0, 0, 'Perfect! Oh, they sparkle so beautifully! These fragments are incredible. Here, take this for your troubles. And if I invent something amazing, you’ll get the first version!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50066;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50066, 2, 0, 0, 0, 0, 0, 0, 0, 'Done? You’re amazing! Without you, this place would’ve been an icy disaster. Here, take this—you’ve earned it. And if Aqualon comes back, I know who to call!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50067;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50067, 2, 0, 0, 0, 0, 0, 0, 0, 'Githyiss and her brood were corrupted by this totem. This is a Furbolg totem.$B$BThis is indeed very troubling. It means that the Gnarlpine still pose a threat to this forest. We felt secure far too early.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50068;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50068, 2, 0, 0, 0, 0, 0, 0, 0, 'Good. I know this was probably not the most pleasant task, but we are very grateful to you.$B$BI fear for our beloved Teldrassil... it seems to grow more corrupted with each passing day, and our ignorance of the cause weighs heavily on me.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50069;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50069, 2, 0, 0, 0, 0, 0, 0, 0, 'Praise Elune!$B$BEverything I have encountered points to the corruption being deeply buried within Teldrassil itself... I feared we would never be able to strike at the source without killing the World Tree. I am so grateful to have been wrong!$B$BWe owe this fortunate turn of events to Malfurion. Perhaps I will seek him out before I return to the wilderness.$B$BThank you, $N. Thank you for everything you have done. Surely, you will one day become a great hero!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50070;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50070, 2, 0, 0, 0, 0, 0, 0, 0, 'It has been undeniably a great honor to have met you. You are living proof of the nobility of your people.$B$BGo now and finish this final task. Take the water to Tyrande Whisperwind in Darnassus.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50071;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50071, 2, 0, 0, 0, 0, 0, 0, 0, 'It\'s about time we got some supplies through here. This city would fall apart if we lost our trade with Orgrimmar.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50072;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50072, 2, 0, 0, 0, 0, 0, 0, 0, 'The Grimtotem are attacking and threatening this settlement. We need all the help we can get!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50073;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50073, 2, 0, 0, 0, 0, 0, 0, 0, 'You fought well, but this battle is far from over.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50074;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50074, 2, 0, 0, 0, 0, 0, 0, 0, 'This grain will go a long way in ensuring our survival. You have our thanks.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50075;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50075, 2, 0, 0, 0, 0, 0, 0, 0, 'Without their leader, the Grimtotem will scatter. You have done a great service today.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50076;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50076, 2, 0, 0, 0, 0, 0, 0, 0, 'I knew the Earthmother would not abandon me. Thank you $R.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50077;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50077, 2, 0, 0, 0, 0, 0, 0, 0, 'You have brought Ahuna back safely. We are deeply grateful - without you, she would have been lost. The Grimtotem will answer for their crimes, but today, we celebrate her return.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50078;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50078, 2, 0, 0, 0, 0, 0, 0, 0, 'Sludgebeast’s death will bring peace to the land, at least for a time.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50079;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50079, 2, 0, 0, 0, 0, 0, 0, 0, 'You did it? Thunderfang is dead? Incredible... The Barrens are a little safer now, thanks to you.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50080;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50080, 2, 0, 0, 0, 0, 0, 0, 0, 'The kodo has arrived safely? Well done. It seems our business partner has no reason to complain today.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50081;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50081, 2, 0, 0, 0, 0, 0, 0, 0, 'Ah yes... I see a cunning in your eyes that was not present before. Well done, $C.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50082;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50082, 4, 0, 0, 0, 0, 0, 0, 0, 'Thank you, $C. Some of them surely would not have made it without you.$B$BWe\'ll be taking the wounded to the main building once we have them all safely back in town.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50083;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50083, 2, 0, 0, 0, 0, 0, 0, 0, 'Your efforts are very much appreciated. Since the storm, the elementals have been out of control. We have little hope of taking back Auberdine without extinguishing their presence first.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50084;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50084, 2, 0, 0, 0, 0, 0, 0, 0, '<The machine crackles loudly, and then Wizbang\'s static-ridden voice becomes audible.>$B$BLovely! I hope you didn\'t kill too many of them. We\'re not trying to wipe out the population, we just need to get to the source of this corruption quickly! I\'m proceeding with the analysis now.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50085;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50085, 2, 0, 0, 0, 0, 0, 0, 0, 'This definitely looks like the stuff they were eating. Let me take a closer peek...', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50086;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50086, 2, 0, 0, 0, 0, 0, 0, 0, '<Static precedes Wizbang\'s distorted voice.>$B$BThe results are coming in now. We should have some information on how the bears contracted this in just a moment.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50087;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50087, 2, 0, 0, 0, 0, 0, 0, 0, 'It is done then. Thank you. I only hope that the wildlife here will see more peace with his passing.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50088;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50088, 2, 0, 0, 0, 0, 0, 0, 0, 'This should be plenty! Thanks so much! I\'ll get working on a cure for this nastiness immediately.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50089;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50089, 2, 0, 0, 0, 0, 0, 0, 0, 'You have our thanks. It is time that we leave the past behind us, and start looking to our future.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50090;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50090, 2, 0, 0, 0, 0, 0, 0, 0, 'Agony abounds, friend! The land itself threatens to devour us, and yet we persist. I wonder why that is? With this doomweed and the guts and gore that the apothecary needs, we might have something suitable to send to the Forsaken.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50091;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50091, 2, 0, 0, 0, 0, 0, 0, 0, 'Well done. These scouts thought they could lurk in the shadows and strike whenever they pleased. Now they know that Silverpine belongs to us - not to them.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50092;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50092, 2, 0, 0, 0, 0, 0, 0, 0, 'Well done. Maybe next time they’ll think twice before stepping into our territory. But I have a feeling this won’t be the last we hear of them...', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50093;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50093, 2, 0, 0, 0, 0, 0, 0, 0, 'You killed the Skitterweb matriarch? We owe you a great debt of gratitude. You\'ve saved us all from certain execution, $N.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50094;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50094, 2, 0, 0, 0, 0, 0, 0, 0, 'This isn’t much, but at least we’ve recovered something. With every loss we suffer, those Worgen get bolder. We need to hit them back before they take everything from us.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50095;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50095, 2, 0, 0, 0, 0, 0, 0, 0, 'That should send a message. Without their leader, the Worgen will be left scrambling, and our caravans might actually make it through in one piece. Good work, $C.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50096;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50096, 2, 0, 0, 0, 0, 0, 0, 0, 'No pleas for mercy, no second chances. That is the way of the Forsaken. The fewer Gilneans reach safety, the less we have to fight later. Well done, $C.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50097;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50097, 2, 0, 0, 0, 0, 0, 0, 0, 'Good work! At least now we won\'t have to worry about our own weapons being used against us. Still, I wouldn’t be surprised if those vermin try again...', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50098;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50098, 2, 0, 0, 0, 0, 0, 0, 0, 'That should teach them a lesson. Maybe now we can get some supplies through without being robbed blind.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50099;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50099, 2, 0, 0, 0, 0, 0, 0, 0, 'Good work, $C. But something tells me we haven’t seen the last of them...', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50100;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50100, 2, 0, 0, 0, 0, 0, 0, 0, 'Nice work, $C. This will make travel a bit safer... and keep us warm at night!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50101;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50101, 2, 0, 0, 0, 0, 0, 0, 0, 'That should keep them away for now. I just hope we haven’t seen the start of something worse...', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50102;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50102, 2, 0, 0, 0, 0, 0, 0, 0, 'Ha! That’ll teach those slimy thieves to steal from us! These fish are still fresh - we can get them back to the village before they spoil. You did good work today, $C', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50103;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50103, 2, 0, 0, 0, 0, 0, 0, 0, 'Excellent work, $C! Without their leader, the Troggs should be much easier to deal with.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50104;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50104, 2, 0, 0, 0, 0, 0, 0, 0, 'Now that’s what I call a job well done. Thelsamar is safer thanks to you, $C!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50105;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50105, 2, 0, 0, 0, 0, 0, 0, 0, 'Edrin made it back in one piece? That\'s a relief. If the Troggs are bold enough to start taking prisoners, things are worse than I thought...$B$BYou did good work today, $C. Thelsamar could use more dwarves which are like you.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50106;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50106, 2, 0, 0, 0, 0, 0, 0, 0, 'Haha! You actually found it! Well done, $C. And you still have all your limbs? Then you fared better than I did! If I were you, I’d steer clear of those waters for a while...', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50107;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50107, 2, 0, 0, 0, 0, 0, 0, 0, 'It’s not much, but it will keep us going for another day. Thank you, $C. I hope you understand... this land is dying, and so are its people.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50108;
