INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50161, 2, 0, 0, 0, 0, 0, 0, 0, 'You’ve done a good thing today, $N. Lakeshire owes you a great deal.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50162;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50162, 2, 0, 0, 0, 0, 0, 0, 0, 'I knew it! Looks like Snarltooth is behind this uprising - well, looks like we got ourselves another Gnoll to kill.$B$BMagistrate Solomon must be notified.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50163;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50163, 2, 0, 0, 0, 0, 0, 0, 0, '<Magistrate Solomon puts on his monocle.>$B$BOh, you\'re not Conacher. What have you got there?$B$B<Magistrate Solomon takes Parker\'s report from you.>$B$BOh for pity\'s sake. Snarltooth?', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50164;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50164, 2, 0, 0, 0, 0, 0, 0, 0, 'Excellent! Let me make a few adjustments here and we should be good to go.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50165;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50165, 2, 0, 0, 0, 0, 0, 0, 0, 'You\'ve tuned it? Alright! Let\'s go to work!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50166;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50166, 2, 0, 0, 0, 0, 0, 0, 0, 'Good work, $N. I\'ve just been handed some important information.$B$BI\'ve transferred a few silver over to you for the gnome, erm, gnoll kills.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50167;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50167, 2, 0, 0, 0, 0, 0, 0, 0, 'Wonderful! Hopefully the gnolls will put their attack plans on hold for now.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50168;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50168, 2, 0, 0, 0, 0, 0, 0, 0, 'Thank you, citizen. Without you, we would have starved or worse, been forced to go out and get food for ourselves.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50169;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50169, 2, 0, 0, 0, 0, 0, 0, 0, 'Impressive, $N. It would seem that you are capable enough to handle yourself. Perhaps a more suitable challenge could be found for one of your abilities.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50170;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50170, 2, 0, 0, 0, 0, 0, 0, 0, '$N, to be honest with you, I did not believe that you would get this far, but you are clearly a $C to be reckoned with. In fact, if you wish to formally join Master Carevin\'s struggle, I will gladly write for you a letter of recommendation.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50171;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50171, 2, 0, 0, 0, 0, 0, 0, 0, 'You performed well, $N. But even now, years after their first arrival, there seem to be so many to replace the ones we kill.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50172;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50172, 2, 0, 0, 0, 0, 0, 0, 0, 'So... Calor finally sees it too. The worgen are changing, becoming something far worse than what we once faced.$B$BYou came just in time, $N. There’s much to do, and little time to act.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50173;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50173, 2, 0, 0, 0, 0, 0, 0, 0, 'Excuse me for my cold reception, $N, but as I\'m sure you can tell, I am an extremely busy man. I see that you have impressed Calor--and I assure you, that is no small feat--and that he has given his recommendation.$B$BThere are unsavory types afoot, $N, and we can use the help of all who have proven themselves. We battle against demons, the undead, and those who would provide them aid. Be vigilant, be wary, and trust none who would not give aid to our cause.$B$BGlory under the Light.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50174;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50174, 2, 0, 0, 0, 0, 0, 0, 0, 'So it is done. Shadowfang fought to the last, as we knew he would.$B$BWe may never fully understand what happened in those tunnels, but at least his bloodlust will no longer plague these lands.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50175;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50175, 2, 0, 0, 0, 0, 0, 0, 0, 'These caves will never be free of poison, but at least the worst of them are dead.$B$BYou’ve done well, $N. Perhaps there’s still a future for Moonveil Quarry after all.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50176;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50176, 2, 0, 0, 0, 0, 0, 0, 0, 'So Velthara has fallen. The brood will wither without their mother, and the quarry may yet be reclaimed.$B$BYou have done a great thing today, $N. The people of Darkshire may never know what you saved them from, but I do.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50177;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50177, 2, 0, 0, 0, 0, 0, 0, 0, 'Sounds like a close call! But he ran off without harming you further...$B$BHe might be still fighting it. Master Harris must know!', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50178;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50178, 2, 0, 0, 0, 0, 0, 0, 0, 'Welcome to our humble camp. I wouldn\'t call it pleasant, but it\'s at least somewhat private.$B$BBeg your pardon? A worgen at the farm?', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50179;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50179, 2, 0, 0, 0, 0, 0, 0, 0, 'It worked! You saw them, didn’t you? Hidden in the trees, watching us all this time...$B$BThis is only the beginning, $N. If the ampule works, we may finally have a way to strike first—to hunt them before they hunt us.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50180;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50180, 2, 0, 0, 0, 0, 0, 0, 0, 'Good. With every worgen slain, Duskwood becomes a little safer.$B$BBut I fear this fight is far from over. The more we learn about their curse, the closer we come to breaking it... or becoming its next victims.', 16000);
DELETE FROM `quest_offer_reward` WHERE `ID`=50181;
INSERT INTO `quest_offer_reward` (`ID`, `Emote1`, `Emote2`, `Emote3`, `Emote4`, `EmoteDelay1`, `EmoteDelay2`, `EmoteDelay3`, `EmoteDelay4`, `RewardText`, `VerifiedBuild`) VALUES (50181, 2, 0, 0, 0, 0, 0, 0, 0, 'Good. She’s one of the few in Darkshire who sees the truth about this curse.$B$BBut tell me, $N... when you walked through Raven Hill, did you feel it?$B$BThe worgen don’t just lurk in the shadows. They are the shadows. And they are watching.', 16000);
