INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50004, 'deDE', 'Ein Bericht vom Glutnebelgipfel? Es scheint, als ob die Zwielichtkultisten uns hier keine Ruhe gönnen. Schon gibt es Berichte über verdächtige Bewegungen am Rande des Brachlands. Das Zwielicht erhebt sich erneut, und dies ist erst der Anfang...', 16000);
