INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50006, 'deDE', 'Du hast einen Kristall des Zwielichts erlangt? Hervorragend. Die Dunkelheit, die von diesem Kristall ausgeht, ist spürbar – er muss von mächtiger Magie durchzogen sein. Mit diesem Artefakt können wir vielleicht endlich die Pläne des Kults enthüllen.', 16000);
