INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50002, 'deDE', 'Das sollte ihre Kräfte deutlich schwächen. Jetzt sind sie nicht mehr so zahlreich und damit leichter zu besiegen.', 16000);
