INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50003, 'deDE', 'Zerak ist gefallen? Hervorragend! Das wird den Kult in Verwirrung stürzen und ihre Pläne hier im Brachland zunichtemachen.', 16000);
