INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50029, 'deDE', 'Kommandant Vorthalax ist tot? Fantastisch! Das wird dem Zwielichtkult einen schweren Schlag versetzen. Die Horde schuldet dir einen großen Dank.', 16000);
