INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50030, 'deDE', 'Treffer! Das ist der Skorpid-Saft, auf den ich gewartet hab! Damit bring ich dich wieder in Topform, $c. Glaub mir, Goblin-Technik ist unschlagbar!', 16000);
