INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50031, 'deDE', 'Du hast sie erwischt? Gut, gut! Das wird diesen übergroßen Guppys eine Lehre sein, sich mit meinem Geschäft anzulegen. Du bist in Ordnung, Kleiner. Hier, nimm das. Hast du dir verdient!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50032 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50032, 'deDE', 'Na gut, hier ist das Geld... aber du solltest besser verschwinden, bevor ich meine Haltung gegenüber dem Jagen von Horde-Verrätern noch einmal überdenke.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50033 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50033, 'deDE', 'Ich habe dir da draußen beim Kämpfen zugesehen. Wie lange bist du schon ein $c? Du bist in Ordnung, Kleiner.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50034 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50034, 'deDE', 'Du hast ihn im Kampf mühelos besiegt, hier ist dein Geld.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50035 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50035, 'deDE', 'Flakk schickt Euch? Ohne Euch zu sagen, was ich brauche? Keine Rede von Wagenrädern, Nägeln und Vorräten? So ein Depp.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50036 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50036, 'deDE', 'Hm… ja, das ergibt Sinn. Die Worgen strömen nur so aus Gilneas heraus. Ich wusste, es wäre nur eine Frage der Zeit, bis sie hier in Tirisfal ankommen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50037 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50037, 'deDE', 'Hoho! Das sind sie! Ich wusste, du wärst der richtige für diesen Job. Diese Babys bringen mich zum großen Geld. Hier, nimm das - hast es dir verdient!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50038 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50038, 'deDE', 'Du hast eine schwere Bürde getragen, doch du hast unsere Welt ein Stück sicherer gemacht. Der Erdgeist ist fort, und wir schulden dir unseren Dank.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50039 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50039, 'deDE', 'Das ist sie! Meine wunderschöner, glitzernder, profitbringender Schatz! Ich wusste, ich kann mich auf dich verlassen. Hier, nimm deinen Anteil. Ich habe bald ein Vermögen zu zählen!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50040 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50040, 'deDE', 'Ihr habt gute Arbeit geleistet, um die Bedrohung durch die Schattenpfoten einzudämmen. Nehmt diese Belohnung für eure Tapferkeit an.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50041 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50041, 'deDE', 'Ihr habt dort Erfolg gehabt, wo viele gescheitert sind. Nehmt diese Belohnung.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50042 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50042, 'deDE', 'Hoho! Das sind sie! Ich wusste, dass ihr die richtige Person für den Job seid. Diese Schönheiten werden mich reich machen. Hier, nehmt das - ihr habt es euch verdient!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50043 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50043, 'deDE', 'Ihr habt eine schwere Bürde auf euch genommen, aber ihr habt unsere Welt ein Stück sicherer gemacht. Der Erdgeist ist besiegt, und wir schulden euch unseren Dank.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50044 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50044, 'deDE', 'Das ist es! Mein wunderschöner, glänzender Schatz! Ich wusste, dass ich auf euch zählen kann. Hier, nehmt euren Anteil - ich habe einiges an Gewinn zu zählen!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50045 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50045, 'deDE', 'Danke für euer schnelles Handeln. Der östliche Turm hält dank eurer Bemühungen stand. Die Menschen im Wald von Elwynn schulden euch viel.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50046 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50046, 'deDE', 'Ihr habt den Menschen im Wald von Elwynn einen großen Dienst erwiesen. Nehmt dies als Zeichen unserer Anerkennung.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50047 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50047, 'deDE', 'Die Spione wurden beseitigt? Hervorragende Arbeit! Ihr habt den Schwarzfels-Truppen einen entscheidenden Schlag versetzt.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50048 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50048, 'deDE', 'Eure Tapferkeit bei der Beseitigung dieser Assassinen hat euch diese Belohnung eingebracht. Gut gemacht!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50049 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50049, 'deDE', 'Eure Reise hierher fasziniert mich. Ich werde euch meine Geschichte erzählen, wenn ihr bereit seid, mir zuerst zu helfen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50050 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50050, 'deDE', 'Ihr habt ihn gefunden? Mein Ring! Aber er ist beschädigt… trotzdem danke ich euch, dass ihr ihn zurückgebracht habt.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50051 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50051, 'deDE', 'Ah, dieser Ring ist faszinierend! Die Handwerkskunst ist unvergleichlich. Es wird etwas Arbeit erfordern, aber ich kann ihn reparieren.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50052 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50052, 'deDE', 'Ihr habt ihn zurückgebracht... in perfektem Zustand!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50053 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50053, 'deDE', 'Danke, dass ihr dies überbracht habt. Elrics Worte bedeuten mehr, als ihr euch vorstellen könnt. Ich werde dafür sorgen, dass seine Wünsche erfüllt werden.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50054 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50054, 'deDE', 'Beeindruckende Arbeit, mein Junge. Von jetzt an können wir die Stellung wieder alleine halten.$B$BHier, nehmt das für Eure Bemühungen. Es könnte sich als nützlich erweisen, zumal ich Euch um etwas noch Wichtigeres bitten muss...', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50055 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50055, 'deDE', 'Hmm... Sie machen uns dafür verantwortlich, ihre Höhle zerstört zu haben, sagt Ihr? Das waren nicht wir, das war dieses schreckliche Erdbeben heute früh. Sind die so beschränkt, dass sie nicht sehen, dass unser Lager auch beinah zerstört wurde?$B$BIch kann allerdings keinem Trogg vorwerfen, dass er für seine Heimat kämpft.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50056 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50056, 'deDE', 'Ich habe mir schon gedacht, dass sie nicht wegen der Aussicht hier sind. Das ist ernster als befürchtet! Dies ist nicht nur ein Überfall. Die Dunkeleisenzwerge wollen die Verteidigungsbollwerke von Eisenschmiede lahmlegen! Während wir hier kämpfen, haben sie den Flugplatz von Eisenschmiede angegriffen. Wir müssen sofort handeln, bevor sie noch mehr Schaden anrichten!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50057 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50057, 'deDE', 'Zum Glück seid Ihr hier, $N. Der Flugplatz wird von Dunkeleisenzwergen angegriffen. Wir halten nicht mehr lange durch.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50058 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50058, 'deDE', 'Gut gemacht! Meine Männer arbeiten hart daran, unsere Flugmaschinen und die Dampfpanzer kampfbereit zu machen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50059 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50059, 'deDE', 'Die Soldaten entdecken verborgene Reserven der Stärke und des Mutes und werfen sich den Dunkeleisenzwergen entgegen. Solange wir hier sind, wird der Flugplatz nicht fallen!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50060 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50060, 'deDE', 'Das Blatt wendet sich, $N, größtenteils dank Eurer Leistungen. Jetzt müssen wir den Anführern der Dunkeleisenzwerge einen Schlag versetzen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50061 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50061, 'deDE', 'Geschafft! Ohne Eure Hilfe hätte der Feind den Flugplatz von Eisenschmiede erobert. Dann hätten die Dunkeleisenzwerge freie Bahn bis Eisenschmiede gehabt.$B$BDer Rat der Drei Hämmer muss erfahren, was hier geschehen ist.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50062 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50062, 'deDE', 'Euer Bericht über die Angriffe der Dunkeleisenzwerge ist besorgniserregend, aber eure Tapferkeit hat Eisenschmiede einen schweren Schlag erspart. Gut gemacht, $N. Wir werden die Verteidigung verstärken und sicherstellen, dass wir auf jede Bedrohung vorbereitet sind. Ihr habt unserer Sache treu gedient!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50063 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50063, 'deDE', 'Ah, gut, dass ihr da seid, $C. Kommandant Steinbrecher hat euch geschickt? Ausgezeichnet. Es gibt viel zu tun, und keine Zeit zu verlieren. Mit eurer Hilfe können wir diesen Flugplatz halten und den Dunkeleisenzwergen zeigen, dass wir nicht so leicht zu besiegen sind!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50064 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50064, 'deDE', 'Bemerkenswert! Ich wusste, dass Ihr ein wahrer Held von Eisenschmiede seid, $N. Mit der Niederlage von Frostbann ist die Sicherheit unserer Soldaten und des Flugplatzes gewährleistet. Dieser Sieg wird in Tavernen von Dun Morogh bis Sturmwind besungen werden. Nehmt dies als Zeichen unserer Dankbarkeit.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50065 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50065, 'deDE', 'Perfekt! Oh, sie funkeln so wunderschön! Diese Fragmente sind unglaublich. Hier, nimm das für deine Mühen. Und wenn ich etwas Erstaunliches erfinde, bekommst du die allererste Version!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50066 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50066, 'deDE', 'Erledigt? Du bist ja großartig! Ohne dich wäre das hier in einer eisigen Katastrophe geendet. Hier, das hast du dir verdient. Und wenn Aqualon wiederkommt, weiß ich, wen ich rufen muss!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50067 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50067, 'deDE', 'Githyiss und ihre Brut wurden von diesem Totem verdorben. Dies ist ein Totem der Furbolgs.$B$BDies ist tatsächlich sehr beunruhigend. Es bedeutet, dass die Knarzklauen noch immer eine Gefahr für diesen Wald darstellen. Wir haben uns zu früh zu sicher gefühlt.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50068 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50068, 'deDE', 'Gut. Ich weiß, dass das wahrscheinlich nicht gerade angenehme Arbeit war, aber wir sind Euch sehr dankbar.$B$BIch fürchte um unseren geliebten Teldrassil... es sieht aus, als werde er von Tag zu Tag verderbter, und unsere Unkenntnis der Ursache quält mich sehr.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50069 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50069, 'deDE', 'Elune sei gepriesen!$B$BAlles, worauf ich gestoßen bin, deutet darauf hin, dass die Verderbnis tief in Teldrassil selbst vergraben liegt... Ich hatte schon befürchtet, wir würden die Quelle niemals angreifen können, ohne den Weltenbaum dabei zu töten. Ich bin so dankbar, dass ich falsch lag!$B$BDiese günstige Wendung des Schicksals haben wir Malfurion zu verdanken. Vielleicht werde ich ihn aufsuchen, bevor ich wieder in die Wildnis aufbreche.$B$BVielen Dank, $N. Danke für alles, was Ihr getan habt. Sicherlich werdet Ihr eines Tages eine großer Held sein!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50070 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50070, 'deDE', 'Es war zweifellos eine große Ehre, Euch kennengelernt zu haben. Ihr seid der lebende Beweis dafür, wie nobel Euer Volk ist.$B$BGeht jetzt und beendet diese letzte Aufgabe. Bringt das Wasser zu Tyrande Wisperwind in Darnassus.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50071 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50071, 'deDE', 'Es wurde aber auch Zeit, dass ein paar Vorräte bis hierher durchkommen. Diese Stadt würde in die Binsen gehen, wenn wir unseren Handel mit Orgrimmar verlören.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50072 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50072, 'deDE', 'Die Grimmtotem greifen an und bedrohen diese Siedlung. Wir brauchen jede Hilfe, die wir bekommen können!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50073 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50073, 'deDE', 'Du hast tapfer gekämpft, aber dieser Kampf ist noch lange nicht vorbei.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50074 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50074, 'deDE', 'Dieses Getreide wird wesentlich zu unserem Überleben beitragen. Du hast unseren Dank.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50075 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50075, 'deDE', 'Ohne ihren Anführer werden die Grimmtotem auseinanderfallen. Du hast heute Großes geleistet.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50076 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50076, 'deDE', 'Ich wusste, dass die Erdenmutter mich nicht verlassen würde. Danke, $R.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50077 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50077, 'deDE', 'Du hast Ahuna sicher zurückgebracht. Wir sind dir zutiefst dankbar – ohne dich wäre sie verloren gewesen. Die Grimmtotem werden für ihre Taten bezahlen, aber heute feiern wir ihre Rückkehr.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50078 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50078, 'deDE', 'Schlickbesties Tod wird dem Land zumindest für eine Weile Frieden bringen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50079 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50079, 'deDE', 'Du hast es geschafft? Donnerzahn ist tot? Unglaublich... Das Brachland ist nun ein Stück sicherer, dank dir.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50080 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50080, 'deDE', 'Der Kodo ist wohlbehalten angekommen? Gut gemacht. Es scheint, als hätte unser Geschäftspartner heute keinen Grund zur Beschwerde.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50081 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50081, 'deDE', 'Ah ja... Ich sehe eine Gerissenheit in Euren Augen, die dort vorher nicht zu finden war. Gut gemacht, $C.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50082 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50082, 'deDE', 'Danke, $C. Einige von ihnen hätten es ohne euch sicher nicht geschafft.$B$BWir bringen die Verwundeten ins Hauptgebäude, sobald wir sie alle sicher in die Stadt zurückgebracht haben.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50083 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50083, 'deDE', 'Eure Mühen werden sehr geschätzt. Seit dem Sturm sind die Elementare völlig außer Kontrolle. Ohne ihre Vernichtung haben wir kaum Hoffnung, Auberdine jemals zurückzuerobern.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50084 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50084, 'deDE', '<Die Maschine knistert laut, dann wird Wizbangs statisch verzerrte Stimme hörbar.>$B$BHervorragend! Ich hoffe, ihr habt nicht zu viele von ihnen getötet. Wir wollen die Population nicht auslöschen – wir müssen nur schnell die Quelle dieser Verderbnis finden! Ich beginne jetzt mit der Analyse.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50085 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50085, 'deDE', 'Das sieht definitiv nach dem Zeug aus, das sie gefressen haben. Lasst mich das genauer untersuchen...', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50086 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50086, 'deDE', '<Statische Geräusche gehen Wizbangs verzerrter Stimme voraus.>$B$BDie Ergebnisse kommen gerade rein. Wir sollten in wenigen Augenblicken wissen, wie sich die Bären damit infiziert haben.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50087 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50087, 'deDE', 'Es ist also getan. Danke. Ich hoffe nur, dass die Tierwelt hier nun etwas mehr Frieden finden wird.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50088 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50088, 'deDE', 'Das sollte mehr als genug sein! Vielen Dank! Ich werde sofort mit der Entwicklung eines Heilmittels für diese Plage beginnen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50089 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50089, 'deDE', 'Ihr habt unseren Dank. Es ist an der Zeit, die Vergangenheit hinter uns zu lassen und in die Zukunft zu blicken.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50090 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50090, 'deDE', 'Schmerz im Überfluss, sage ich Euch! Das Land selbst droht, uns zu verschlingen und dennoch bestehen wir fort. Ich frage mich, warum das so ist? Mit diesem Verdammniskraut, den Eingeweiden und dem geronnenen Blut, das die Apotheker benötigen, haben wir vielleicht etwas Geeignetes, das wir an die Verlassenen senden können.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50091 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50091, 'deDE', 'Gut gemacht. Diese Späher dachten, sie könnten sich in den Schatten verstecken und zuschlagen, wann immer es ihnen passt. Jetzt wissen sie, dass der Silberwald uns gehört - nicht ihnen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50092 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50092, 'deDE', 'Gut gemacht. Vielleicht überlegen sie es sich beim nächsten Mal zweimal, bevor sie sich in unser Gebiet wagen. Aber ich habe das Gefühl, dass dies nicht das letzte Mal war, dass wir von ihnen hören...', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50093 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50093, 'deDE', 'Habt ihr die Netzhuschermatriarchin getötet? Gut.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50094 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50094, 'deDE', 'Das ist nicht viel, aber zumindest haben wir etwas zurückgewonnen. Mit jeder verlorenen Lieferung werden diese Worgen dreister. Wir müssen zurückschlagen, bevor sie uns alles nehmen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50095 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50095, 'deDE', 'Das sollte eine klare Botschaft senden. Ohne ihren Anführer sind die Worgen orientierungslos, und unsere Karawanen könnten es endlich unversehrt durch das Gebiet schaffen. Gute Arbeit, $C.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50096 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50096, 'deDE', 'Keine Bitten um Gnade, keine zweite Chance. So ist der Weg der Verlassenen. Je weniger Gilneer ihr Ziel erreichen, desto weniger müssen wir später bekämpfen. Gut gemacht, $C.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50097 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50097, 'deDE', 'Gute Arbeit! Zumindest müssen wir uns jetzt keine Sorgen machen, dass unsere eigenen Waffen gegen uns verwendet werden. Trotzdem würde es mich nicht wundern, wenn dieses Gesindel es erneut versucht...', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50098 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50098, 'deDE', 'Das sollte ihnen eine Lektion sein. Vielleicht können wir jetzt endlich Nachschub bekommen, ohne komplett ausgeraubt zu werden.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50099 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50099, 'deDE', 'Gute Arbeit, $C. Aber irgendetwas sagt mir, dass wir noch nicht das Letzte von ihnen gesehen haben... ', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50100 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50100, 'deDE', 'Gut gemacht, $C. Das wird das Reisen etwas sicherer machen... und uns nachts warm halten!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50101 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50101, 'deDE', 'Das sollte sie fürs Erste fernhalten. Ich hoffe nur, dass das nicht erst der Anfang von etwas Schlimmerem war...', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50102 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50102, 'deDE', 'Ha! Das wird diese glitschigen Diebe lehren, nicht von uns zu stehlen! Der Fisch ist noch frisch – wir bringen ihn zurück ins Dorf, bevor er verdirbt. Gute Arbeit heute, $C!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50103 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50103, 'deDE', 'Ausgezeichnete Arbeit, $C! Ohne ihren Anführer sollten die Troggs wesentlich einfacher zu besiegen sein.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50104 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50104, 'deDE', 'Jetzt das nenne ich eine gut erledigte Arbeit! Thelsamar ist dank dir ein Stück sicherer, $C!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50105 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50105, 'deDE', 'Edrin hat es unversehrt zurückgeschafft? Das ist eine Erleichterung. Wenn die Troggs bereits kühn genug sind, Gefangene zu nehmen, dann steht es schlimmer um uns, als ich dachte...$B$BDu hast heute gute Arbeit geleistet, $C. Thelsamar könnte mehr Zwerge wie dich gebrauchen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50106 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50106, 'deDE', 'Haha! Du hast ihn wirklich gefunden! Gut gemacht, $C. Und du hast noch alle Gliedmaßen? Dann hast du es besser überstanden als ich! Wenn ich du wäre, würde ich mich eine Weile von diesen Gewässern fernhalten...', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50107 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50107, 'deDE', 'Es ist nicht viel, aber es wird uns für einen weiteren Tag über Wasser halten. Danke, $C. Ich hoffe, Ihr versteht... dieses Land stirbt, und mit ihm seine Menschen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50108 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50108, 'deDE', 'Wir haben endlich etwas zu essen, das nicht morgen schon verschwunden ist. Aber was passiert nächste Woche? Nächsten Monat? Sie haben uns im Stich gelassen, $C. Wir haben vielleicht keine andere Wahl, als uns zu nehmen, was wir brauchen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50109 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50109, 'deDE', 'Wir haben ihre Stadt gebaut. Wir haben ihre Kriege geführt. Und als die Ernten ausblieben...', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50110 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50110, 'deDE', 'Du bist zurück! Aber ich fürchte, ich habe schlechte Nachrichten.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50111 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50111, 'deDE', 'Wir haben das Schlimmste verhindert, aber das war kein zufälliger Angriff. Sie testen uns, um zu sehen, wie weit sie gehen können, bevor wir zusammenbrechen.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50112 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50112, 'deDE', 'Das sollte sie verlangsamen, aber das hier ist noch lange nicht vorbei. Wir müssen herausfinden, wer sie anführt – und zwar schnell.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50113 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50113, 'deDE', 'Ha! Jetzt haben wir sie endlich!$B$BDas ist genau die Information, die wir gebraucht haben. Mit ihren Anführern enttarnt, wird ihre Rebellion nicht mehr lange andauern.$B$BIhr habt gute Arbeit geleistet. Das ändert alles!', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50114 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50114, 'deDE', 'Eine Bedrohung weniger, um die wir uns sorgen müssen, aber ich fürchte, dieser Kampf ist noch nicht vorbei.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50115 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50115, 'deDE', 'Westfall ist sicher... vorerst. Aber ich fürchte, das ist erst der Anfang.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50116 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50116, 'deDE', 'Die Felder sind sicher... vorerst. Aber wer hat die Golems wieder aktiviert? Das ist es, was ich wissen will.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50117 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50117, 'deDE', 'Der Boden ist still... vorerst. Aber wir müssen herausfinden, was dieses Ding erweckt hat.', 16000);
DELETE FROM `quest_offer_reward_locale` WHERE `ID`=50118 AND `locale`='deDE';
INSERT INTO `quest_offer_reward_locale` (`ID`, `locale`, `RewardText`, `VerifiedBuild`) VALUES (50118, 'deDE', 'Die Flüstern des Seelenlosen verklingen, aber die Luft fühlt sich immer noch... falsch an.', 16000);
