INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50000, 'deDE', 'Eine neue Bedrohung ', 'Dunkle Mächte sammeln sich erneut, und die Zeichen deuten auf das Zwielichtkult. Unsere Späher haben seltsame Aktivitäten im Brachland am Glutnebelgipfel entdeckt. Wir wissen noch nicht, was die Kultisten dort suchen, aber eines ist sicher: Es muss etwas Bedeutsames sein. Wir haben eine Kontaktperson im Brachland – Dalra Faulschleier. Sie kennt die Kultisten und ihre Absichten besser als jeder andere. Finde sie am Glutnebelgipfel und erfahre mehr über das, was vor sich geht. Beeile dich, $N. Wenn der Zwielichtkult etwas vorhat, müssen wir ihnen zuvorkommen.', 'Reise zum Glutnebelgipfel im Brachland und finde Dalra Faulschleier', '', 'Reist zum Glutnebelgipfel im Brachland und findet Dalra Faulschleier', '', '', '', '', 16000);
