INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50001, 'deDE', 'Groll der Erde', 'Die Erdelementare in der Nähe des Glutnebelgipfels zeigen Anzeichen von Unruhe. Etwas scheint sie zu beeinflussen, und wenn wir nicht bald handeln, könnte ihre Aggression eskalieren. Wir müssen sie beseitigen, bevor sie außer Kontrolle geraten. Reise zu den Hängen des Glutnebelgipfels und kümmere dich um die Erdelementare.', 'Tötet 8 Erdelementare in der Nähe des Glutnebelgipfels.', '', 'Kehrt zu Dalra Faulschleier zurück.', '', '', '', '', 16000);
