INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50002, 'deDE', 'Angriff auf die Kultisten', 'Die Zwielichtkultisten sammeln sich am Glutnebelgipfel, und ihre Präsenz wird mit jedem Tag stärker. Bevor wir uns ihrem Anführer stellen, müssen wir ihre Reihen schwächen. Begib dich zum Lager der Zwielichtkultisten und töte so viele wie möglich. Je weniger Diener sie haben, desto leichter wird es, ihren Anführer zu konfrontieren.', 'Tötet Zwielichtanhänger im Lager am Glutnebelgipfel.', '', 'Kehrt zu Dalra Faulschleier zurück.', '', '', '', '', 16000);
