INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50005, 'deDE', 'Unruhige Wälder', 'Die Zwielichtkultisten haben ihre Aufmerksamkeit nun offenbar auf das Eschental gerichtet. Berichte von Splitterholzposten deuten auf zunehmende Angriffe hin, die ganze Patrouillen verschwinden lassen. Unsere Späher haben ungewöhnliche Schattenbewegungen in der Nähe des Postens entdeckt, und die Bewohner sind in Sorge. Reise zum Splitterholzposten im Eschental und finde heraus, was die Zwielichtkultisten dort vorhaben. Jede Information könnte uns helfen, ihre Pläne zu durchkreuzen, bevor sie noch gefährlicher werden.', 'Reise zum Splitterholzposten im Eschental und sammle Informationen über die Aktivitäten der Zwielichtkultisten.', '', 'Reist zum Splitterholzposten im Eschental und sammelt Informationen über die Aktivitäten der Zwielichtkultisten.', '', '', '', '', 16000);
