INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50012, 'deDE', 'Bedrohung unter der Stadt', 'Während du im Eschental beschäftigt warst, hat der Zwielichtkult zugeschlagen – und zwar mitten in Orgrimmar. Berichten zufolge wurde der Flammenschlund, unser alter Trainingsort unter der Stadt, vom Kult eingenommen und besetzt. Es ist unklar, wie weit sie bereits vorgedrungen sind, doch die Bedrohung ist ernst. Melde dich sofort bei Rokh\'nar Wutfell vor dem Flammenschlund. Er wird dir mehr über die Lage berichten.', 'Sprich mit Rokh\'nar Wutfell vor dem Eingang des Flammenschlunds in Orgrimmar.', '', 'Sprecht mit Rokh\'nar Wutfell vor dem Eingang des Flammenschlunds in Orgrimmar.', '', '', '', '', 16000);
