INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50027, 'deDE', 'Bedrohung in Orgrimmar', 'Während sich die Horde mit anderen Bedrohungen abmüht, hat der Zwielichtkult zugeschlagen – mitten im Herzen von Orgrimmar. Berichte deuten darauf hin, dass der Zwielichtshammer sich im Flammenschlund eingenistet hat. Es ist unklar, wie weit sie sich bereits ausgebreitet haben, doch die Gefahr ist ernst. Solche Angelegenheiten dürfen nicht der Horde überlassen werden; die Allianz muss handeln. Melde dich bei Isolde Emberveil hier in Sturmwind. Sie wird dich über die Situation und die notwendigen Maßnahmen informieren.', 'Sprich mit Isolde Glutschleier in Sturmwind.', '', 'Sprecht mit Isolde Glutschleier in Sturmwind.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50028 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50028, 'deDE', 'Zerstörung der Zwielichteier', 'Im Flammenschlund hat der Zwielichtkult einen weiteren abscheulichen Plan in die Tat umgesetzt: Sie haben Zwielichteier gelegt, aus denen ihre verderbten Kreaturen schlüpfen sollen. Diese Eier sind von dunkler Magie durchzogen und dürfen auf keinen Fall schlüpfen.', 'Zerstöre 20 Zwielichteier im Flammenschlund.', '', 'Kehrt zu Isolde Glutschleier zurück.', 'Zwielichteier zerstört', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50029 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50029, 'deDE', 'Die Schlacht gegen Vorthalax', 'Kommandant Vorthalax, der Anführer der Zwielichtkultisten im Flammenschlund, ist die treibende Kraft hinter dieser Besetzung. Er ist mächtig und von der dunklen Magie durchdrungen, die ihm vom Kult verliehen wurde. Solange er lebt, wird der Kult seine Operationen fortsetzen. Dringe tief in den Flammenschlund vor und vernichte Kommandant Vorthalax. Dies wird das Rückgrat der Zwielichtkultisten brechen und ihre Bedrohung in Orgrimmar endgültig beenden.', 'Besiege Kommandant Vorthalax im Flammenschlund.', '', 'Kehrt zu Isolde Glutschleier zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50030 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50030, 'deDE', 'Große Haken und Lächeln sollten Misstrauen hervorrufen', 'Okay, pass auf! Wir schnappen uns ein bisschen Gift von den Skorpionen, die da draußen rumkrabbeln. Aber hey, keine Panik – ich hab alles unter Kontrolle! Ich hab da was gebastelt – ein großes Totem-Dingens. Steck das einfach in den Boden, wenn der Skorpion zustechen will. Das saugt das ganze fiese Gift weg, und du gehst da raus, als wär nichts passiert. Ziemlich genial, oder?', 'Erhaltet 8 Proben Skorpidtoxin, indem Ihr das Totem der Giftextraktion in der Nähe eines giftigen Skorpids aufstellt.', '', 'Kehrt zu Fizzick zwischen Sen\'jin und dem Tal der Prüfungen in Durotar zurück.', 'Probe Skorpidtoxin', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50031 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50031, 'deDE', 'Es ist Hammerzeit!', 'Hey, hör mal zu, Kumpel! Wir haben ein großes Problem da draußen im Wasser. Diese Hammerhaie knabbern nicht mehr nur an Fischen – jetzt sind wir dran! Das sind keine gewöhnlichen Haie, oh nein. Die sind fies, zäh und doppelt so schlau. Aber hey, ein Goblin wie ich hat immer einen Plan! Ich mach’s dir schon schmackhaft, wenn du ein paar von denen erledigst. Aber, äh… geh lieber nicht allein, ja? Diese Fische haben mehr Biss, als du allein verkraften kannst. Also schnapp dir ein paar Freunde und zeig diesen Flossenfressen, wer der Boss ist!', 'Jage 3 Hammerhaie.', '', 'Kehrt zu Fizzick zwischen Sen\'jin und dem Tal der Prüfungen in Durotar zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50032 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50032, 'deDE', 'Gaur Eishorn', 'Ich würde normalerweise keine Taunka jagen, wegen ihrer engen Verbindung zur Horde... aber dieser hier ist ein Verräter und obendrein ein Krimineller. Von seinem eigenen Stamm verstoßen, paddelte er nach Azshara hinunter und wurde dabei erwischt, wie er Vorräte stahl. Gaur ist ein erfahrener Gladiator, $C. Ich wette 10 Silberstücke, dass du es nicht schaffst, ihn zu jagen und zu besiegen.', 'Besiegt Gaur Eishorn im Kampf.', '', 'Kehrt zu Spitzzahn im Scheckeneberhof in Durotar zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50033 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50033, 'deDE', 'Ghislania', 'Ich manage ein paar Gladiatoren, die ich in Wettkämpfen quer durch Kalimdor gegeneinander antreten lasse. Nehmen wir zum Beispiel Ghislania, eine Naga, die an den Küsten von Durotar entlangwandert, um ihre Fähigkeiten zu schärfen. In letzter Zeit hat sie allerdings eine Pechsträhne – drei Kämpfe in Folge verloren. Sie könnte ein wenig Motivation gebrauchen... oder vielleicht einfach nur eine ordentliche Abreibung. Geh zu ihr und fordere sie heraus. Ich wette, selbst DU könntest sie besiegen!', 'Besiegt Ghislania im Kampf.', '', 'Kehrt zu Spitzzahn im Scheckeneberhof in Durotar zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50034 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50034, 'deDE', 'Griswold Hanniston', 'Griswold hat sich in der Burg Tiragarde verschanzt und tut so, als wäre er der Größte mit seinen Allianzkumpels. Er macht Ärger, schnüffelt in unserem Territorium herum und steckt seine Nase in Angelegenheiten, die ihn nichts angehen. Er hält sich für stark, aber ich habe gehört, dass er nur große Töne spuckt. Geh nach Nordwacht und mach ihn fertig. Mal sehen, ob er wirklich so stark ist, wie er vorgibt zu sein.', 'Besiegt Griswold Hanniston im Kampf.', '', 'Kehrt zu Spitzzahn im Scheckeneberhof in Durotar zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50035 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50035, 'deDE', 'Hilfe benötigt', 'Ihr da, $R, nicht so schnell. Mein Boss Spitzzahn hat mich geschickt, um nach Hilfe zu suchen - und nach Vorräten. Ihr scheint kein Händler zu sein, aber wahrscheinlich könnt Ihr ihm trotzdem helfen. Er ist nördlich von hier, neben dem kaputten Wagen. Oh, und sagt ihm, ich habe Euch geschickt... Ich muss sicherstellen, dass er weiß, dass ich hier nicht nur meine Zeit vertrödel.', 'Meldet Euch bei Spitzzahn im Scheckeneberhof in Durotar.', '', 'Meldet Euch bei Spitzzahn im Scheckeneberhof in Durotar.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50036 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50036, 'deDE', 'Aus Gilneas entflohen', 'Wie Ihr Euch vorstellen könnt, ist ein streunender Worg in Tirisfal ein wichtiges Ereignis. Exekutor Zygand wäre daran sicher interessiert.', 'Berichtet Exekutor Zygand in Brill in Tirisfal von der Anwesenheit des Worgen.', '', 'Berichtet Exekutor Zygand in Brill in Tirisfal von der Anwesenheit des Worgen.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50037 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50037, 'deDE', 'Vernichtet die Worgen', 'Wir müssen uns um diese Worgen kümmern, und zwar schnell. Unsere oberste Priorität besteht darin, alle Worgen auszulöschen, die in Brill eingedrungen sind. Ich möchte, dass Ihr hier in Brill nach verdächtigen Vorkommnissen Ausschau haltet, $N. Wir wissen, dass diese Worgen sich in den Schatten verstecken können, also achtet auf jedes noch so kleine Zeichen... Fußspuren, umgedrehte Steine, alles!', 'Findet und tötet 7 Spitzel der Worgen nahe Brill.', '', 'Kehrt zu Exekutor Zygand in Brill in Tirisfal zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50038 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50038, 'deDE', 'Reiten lernen in Orgrimmar', 'Dieser Handzettel bewirbt das bei Levi Ladestock erhältliche Reittraining und Angebote zum Erwerb von Reittieren.$B$BLevi Ladestock befindet sich im Tal der Geister in Orgrimmar.', 'Bringt diesen Handzettel zu Levi Ladestock im Tal der Geister in Orgrimmar.', '', 'Kehrt zu Levi in Durotar zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50039 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50039, 'deDE', 'Reiten lernen in Sturmwind', 'Dieser Handzettel bewirbt das bei Edgar erhältliche Reittraining und Angebote zum Erwerb von Reittieren.$B$BEdgar befindet sich Sturmwind in der Nähe von Olivias Teich.', 'Bringt diesen Handzettel zu Edgar nahe Olivias Teich in Sturmwind.', '', 'Kehrt zu Edgar in Sturmwind zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50040 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50040, 'deDE', 'Säuberung der Schattenpfoten', 'Die Dunkelheit hat ihre Diener entsandt. Die Schattenpfotenworgen terrorisieren die Karrenspur, treiben Wanderer in den Wahnsinn und zerstören alles, was sie finden. Es liegt an dir, diese Gefahr einzudämmen. Gehe hinaus und dezimiere ihre Zahl, bevor sie sich weiter ausbreiten können.', 'Töte 10 Schattenpfotenworgen im Silberwald.', '', 'Kehrt zu Iris Leichenkuss in Brill in Tirisfal zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50041 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50041, 'deDE', 'Dämmerklaue bezwingen', 'Eine größere Gefahr lauert in den Schatten. Dämmerklaue, der Alpha der Schattenpfoten, hat sich in einer Höhle nahe der Karrenspur zurückgezogen. Sein Gebrüll schallt durch die Nacht und nährt die Dunkelheit in den Herzen der Worgen. Wenn er nicht gestoppt wird, werden die Angriffe niemals enden. Erlege Dämmerklaue und bringe seinen Fangzahn als Beweis, dass er keine Gefahr mehr darstellt. Seid jedoch vorsichtig, da im Kampf auch Verbündete rufen könnte...', 'Töte Dämmerklaue und sammle Dämmerklaues Fangzahn.', '', 'Kehrt zu Iris Leichenkuss in Brill in Tirisfal zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50042 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50042, 'deDE', 'Die Weisheit der Ahnen', 'Hey, du siehst aus, als hättest du flinke Hände! Ich habe gehört, dass diese Harpyien hier in Mulgore alte Totems bei sich horten. Exotische Stücke! Stell dir vor, wie viel Gold ich mit denen machen könnte. Bring mir ein paar und ich lasse was für dich springen!', 'Der Reisende Goblin möchte, dass du 6 Uralte Totems aus Harpyien-Lagern der Himmelsschneider sammelst.', '', 'Kehrt zu dem Reisenden Goblin nahe Donnerfels in Mulgore zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50043 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50043, 'deDE', 'Die Rache der Erde', 'Der große Geist der Erde war ein mächtiger Verbündeter, doch dunkle Mächte haben ihn verdorben. Nun wütet er in den Ebenen von Mulgore und gefährdet alles, was wir zu schützen versuchen. Ich fürchte, der Geist kann nicht gerettet werden. Töte ihn, bevor noch mehr Schaden entsteht.', 'Töte den Korrumpierten Erdgeist.', '', 'Kehrt zu dem Reisenden Goblin nahe Donnerfels in Mulgore zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50044 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50044, 'deDE', 'Das gestohlene Vermögen', 'Ich wusste, dass ich dem Kerl nicht trauen konnte! Trixxik hat meine teuerste Ware gestohlen, diese glitzernde goldene Uhr. Die wollte ich für ein Vermögen verkaufen! Ich habe gehört, er versteckt sich irgendwo in oder nahe der Mine der Venture Co. Bring mir die Uhr zurück, und ich lasse dich großzügig am Gewinn teilhaben!', 'Finde Trixxik und bringe den gestohlenen Schatz zurück.', '', 'Kehrt zu dem Reisenden Goblin nahe Donnerfels in Mulgore zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50045 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50045, 'deDE', 'Schließt euch der Schlacht an!', 'Der Wald von Elwynn ist erneut bedroht. Der östliche Turm, der sich in den felsigen Hügeln an der Ostgrenze des Waldes befindet, steht unter Belagerung der Schwarzfels-Truppen. Diese Orks, mit ihren dunklen Bannern, wurden dabei beobachtet, wie sie sich auf einen groß angelegten Angriff vorbereiten. Der Turm ist unsere erste Verteidigungslinie, eingebettet zwischen hohen Eichen und rauen Klippen, und wir können es uns nicht leisten, ihn zu verlieren. Begebt euch nach Osten und helft den Verteidigern, die Eindringlinge zurückzuschlagen. Eure Bemühungen könnten den Unterschied zwischen Sicherheit und Zerstörung für den Wald von Elwynn ausmachen!', 'Meldet euch bei den Verteidigern am östlichen Turm, um gegen die Schwarzfels-Truppen zu helfen.', '', 'Meldet euch bei den Verteidigern am östlichen Turm, um gegen die Schwarzfels-Truppen zu helfen.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50046 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50046, 'deDE', 'Löwen für Lämmer', 'Unruhe braut sich nördlich des Holzfällerlagers zusammen. Die weitläufigen Bauernhöfe, die Nahrung und Schutz für die Menschen im Wald von Elwynn bieten, stehen unter Angriff der Schwarzfels-Worgen. Diese wilden Kreaturen haben damit begonnen, Vieh zu rauben und die Bauern in Angst und Schrecken zu versetzen. Zwischen sanften Hügeln und verstreuten Höfen lauern die Worgen in den Schatten am Waldrand, bereit, erneut zuzuschlagen. Begebt euch nach Norden, vorbei an den verschlungenen Pfaden, und hinein in die dunkleren Teile des Waldes. Beseitigt diese Raubtiere, bevor sie die Existenzgrundlage der Dorfbewohner zerstören!', 'Jagt 8 Worgen des Schwarzfels nördlich des Holzfällerlagers, um die Bauernhöfe zu schützen.', '', 'Kehrt zu Jäger Renwick im östlichen Turm im Wald von Elwynn zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50047 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50047, 'deDE', 'Drängt sie zurück!', 'Die Schwarzfels-Truppen haben unser Land mit listigen Spionen infiltriert. Diese Agenten sind gut ausgebildet und bewegen sich lautlos durch das Unterholz, während sie unsere Verteidigung auskundschaften. Sie wurden nördlich des Holzfällerlagers gesehen, versteckt im hohen Gras und entlang der Baumlinien. Wenn wir ihnen erlauben, Bericht zu erstatten, werden sie unsere Position schwächen und eine größere Invasion ermöglichen. Durchsucht die Gegend sorgfältig und beseitigt diese Spione, bevor sie mit wichtigen Informationen entkommen!', 'Beseitigt 8 Spione des Schwarzfels, um die Gegend zu sichern.', '', 'Kehrt zu Jäger Renwick im östlichen Turm im Wald von Elwynn zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50048 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50048, 'deDE', 'Sie haben Assassinen geschickt', 'Die Schwarzfels-Truppen haben ihre Taktik verschärft und Assassinen tief in den Wald von Elwynn geschickt. Diese geschickten Mörder lauern im Schatten, ihre Klingen mit Gift getränkt, bereit, jeden anzugreifen, der ihre Pläne durchkreuzt. Sie wurden nahe dieses Turmes gesehen, versteckt zwischen alten Eichen und moosbedeckten Felsen. Wir müssen sie ausschalten, bevor sie weiteren Schaden anrichten. Begebt euch in den Wald, findet diese Assassinen und sorgt dafür, dass sie niemandem mehr schaden können.', 'Eliminiere 7 Assassine des Schwarzfels, die sich in den Wäldern nahe des Ostturms verstecken.', '', 'Kehrt zu Jäger Renwick im östlichen Turm im Wald von Elwynn zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50049 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50049, 'deDE', 'Ein mysteriöser Einsiedler', 'Nahe den tosenden Donnerfällen liegt das Zuhause eines mysteriösen Einsiedlers namens Elric. Geschichten über seine Vergangenheit wirbeln wie die Nebel um die Wasserfälle. Die Dorfbewohner behaupten, er sei einst ein Held gewesen, der der Welt den Rücken kehrte. Die Gegend ist friedlich, aber zugleich einschüchternd, mit steilen Klippen und dem ständigen Sprühnebel der Wasserfälle. Finde diesen Einsiedler und enthülle die Wahrheit über seinen Rückzug aus der Gesellschaft. Vielleicht birgt er Geheimnisse, die es wert sind, geteilt zu werden.', 'Reise zu den Donnerfällen und suche Elric, einen Einsiedler, der vielleicht Antworten auf seine geheimnisvolle Vergangenheit hat.', '', 'Reise zu den Donnerfällen und suche Elric, einen Einsiedler, der vielleicht Antworten auf seine geheimnisvolle Vergangenheit hat.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50050 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50050, 'deDE', 'Der verlorene Ring', 'Die Donnerfälle sind nicht nur ein Ort von Schönheit, sondern auch von Gefahr. Irgendwo in der Nähe der tosenden Wasser hat Elric einen Ring verloren, der für ihn von immensem persönlichem Wert ist. Der Ring, so behauptet er, ist nicht nur ein Schmuckstück, sondern ein Familienerbstück, das in Geschichte getränkt ist. Die Gegend um die Wasserfälle ist tückisch, mit rutschigen Felsen und lauernder Wildnis. Suche am Fuß des Wasserfalls, wo der Sprühnebel den Boden durchnässt, und finde diesen verlorenen Schatz für Elric.', 'Durchsuche das gefährliche Terrain der Donnerfälle nach einem Ring, der Elric viel bedeutet.', '', 'Kehrt zu Elric dem Einsiedler bei den Donnerfällen im Wald von Elwynn zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50051 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50051, 'deDE', 'Ein Funken Hoffnung', 'Ah, mein Ring... er hat wahrlich bessere Tage gesehen, nicht wahr? Die Donnerfälle waren nicht freundlich zu ihm, und die Zeit hat ihr Übriges getan. Aber das hier ist kein gewöhnliches Schmuckstück - es wurde aus einer seltenen Legierung gefertigt, und in ihm steckt eine subtile Magie. Ein wahres Meisterwerk, das jedoch Pflege braucht. In Sturmwind gibt es einen Schmied, einen wahren Künstler, der ihn in seinen ursprünglichen Glanz zurückversetzen kann. Folge den Kopfsteinpflasterstraßen durch den Wald von Elwynn in die geschäftige Stadt und suche ihn auf. Bitte, vertrau ihm diesen Schatz an - er bedeutet mir mehr, als ich sagen kann.', 'Bringt den beschädigten Ring zu einem Schmied in Sturmwind. Nur jemand mit großer Fertigkeit kann ihn reparieren.', '', 'Bringt den beschädigten Ring zu einem Schmied in Sturmwind. Nur jemand mit großer Fertigkeit kann ihn reparieren.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50052 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50052, 'deDE', 'Die Wahrheit über den Ring', 'Na, na, dieser Ring, den du mir gebracht hast - das ist ein wahres Meisterwerk. Die Handwerkskunst ist außergewöhnlich, und ich habe ihn in seinen ursprünglichen Zustand zurückversetzen können, soweit ich das beurteilen kann. Aber da ist etwas Merkwürdiges. Dieser Ring ist nicht nur magisch - er trägt eine Aura uralter Magie, etwas, das weit älter ist, als ich erwartet hätte. Sein Design deutet darauf hin, dass er aus einer Zeit stammt, die deutlich weiter zurückliegt, als dein Freund Elric zugegeben hat. Bring ihn zurück zu ihm, und vielleicht findest du die wahre Geschichte hinter diesem geheimnisvollen Erbstück heraus. Und wenn ich das sagen darf: Solche Geschichten werden am besten im tosenden Nebel der Donnerfälle erzählt.', 'Bringt den restaurierten Ring zu Elric zurück und entdecke die Wahrheit hinter seiner Herkunft.', '', 'Bringt den restaurierten Ring zu Elric zurück und entdecke die Wahrheit hinter seiner Herkunft.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50053 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50053, 'deDE', 'Ein letzter Wunsch', 'Du hast ihn zurückgebracht... wiederhergestellt in all seiner Pracht. Dafür schulde ich dir mehr, als Worte ausdrücken können. Aber mit diesem Ring geht es um mehr als nur Magie oder Handwerk - es gibt eine Wahrheit, der ich mich stellen muss. Jahre habe ich diese Last mit mir herumgetragen, und es ist an der Zeit, Wiedergutmachung zu leisten. Hier, nimm diesen Brief. Er ist an einen alten Freund von mir in Goldhain adressiert. Bring ihn bitte dorthin, ja? Der Weg schlängelt sich durch Wiesen und Haine, Orte, die ich oft durchwandert habe. Vielleicht bringt das nicht nur mir, sondern auch uns beiden einen Abschluss.', 'Überbringe Elrics herzliche Nachricht an seinen alten Freund in Goldhain. Es ist das Mindeste, was du für einen Mann tun kannst, der Frieden gefunden hat.', '', 'Überbringe Elrics herzliche Nachricht an seinen alten Freund in Goldhain. Es ist das Mindeste, was du für einen Mann tun kannst, der Frieden gefunden hat.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50054 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50054, 'deDE', 'Die Stellung halten!', 'Hey, Ihr da! Ihr seid doch $N. Würdet Ihr uns helfen?$B$BEs scheint, als ob das heutige Erdbeben eine Gruppe dieser barbarischen Troggs aufgerüttelt hat und die sind über irgendetwas ziemlich erbost. Meine Leute tun, was sie können, um sie fernzuhalten, aber wir könnten Hilfe gebrauchen.$B$BTragt Euren Teil dazu bei - geht Richtung Süden und helft meinen Gebirgsjägern aus der Klemme. Wir müssen die Stellung halten!', 'Tötet 12 Eindringlinge der Felsenkiefertroggs nahe Eisenschmiede.', '', 'Kehrt zu Hauptmann Tret Funkdüse in Stahlrosts Depot in Dun Morogh zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50055 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50055, 'deDE', 'Haut sie!', 'Also gut, hört zu. Ich weiß ein oder zwei Dinge über Troggs, und eins davon ist, dass die größten und stärksten Troggs den meisten Respekt genießen. Diese Rohlinge ziehen Stärke allem anderen vor. Seht Ihr diese großen, starken Troggs hinter ihren Frontlinien? Sie sind das, was einem Anführer am nächsten kommt. Geht und erledigt ein paar von denen, damit sie wissen, was es heißt, sich mit den Töchtern und Söhnen von Eisenschmiede anzulegen!', 'Tötet 5 Stümper der Felsenkiefertroggs nahe Eisenschmiede.', '', 'Kehrt zu Hauptmann Tret Funkdüse in Stahlrosts Depot in Dun Morogh zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50056 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50056, 'deDE', 'Die Heimtücke der Dunkeleisenzwerge', 'Eine Gruppe Dunkeleisenspione hat Eisenbands Truppenlager im Süden, hinter dem Helmsbettsee übernommen. Die Gebirgsjäger wären normalerweise in der Lage, sie aus Dun Morogh herauszuhalten, aber es herrscht gerade Chaos.$B$BDie Dunkeleisenzwerge wären Narren, wenn sie das nicht zu ihrem Vorteil nutzen würden. Wir müssen herausfinden, was sie planen und sie aufhalten, bevor sie Schaden anrichten.$B$BKönntet Ihr versuchen herauszufinden, was sie in Eisenbands Truppenlager machen?', 'Tötet 5 Dunkeleisenspione und Hauptmann Beld im Stützpunkt von Eisenband.', '', 'Kehrt zu Hauptmann Tret Funkdüse in Stahlrosts Depot in Dun Morogh zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50057 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50057, 'deDE', 'Auf zum Flugplatz', 'Die Dunkeleisenzwerge haben bereits einen großangelegten Angriff auf den Flugplatz von Eisenschmiede gestartet! Der Rat hatte den Großteil unserer Truppen nach Dun Morogh verlegt, um die durch die Katastrophe verursachte Instabilität zu bewältigen, wodurch unsere Verteidigung am Flugplatz gefährlich geschwächt wurde.$B$BWir brauchen eine Möglichkeit, dich schnell zum Flugplatz zu bringen. Gehe nach Eisenschmiede und sprich mit Gryth Thurden, dem Greifenmeister. Er wird dir einen Greifen geben, um zum Flugplatz von Eisenschmiede zu fliegen!', 'Miete einen Greifen von Gryth Thurden in Eisenschmiede.', '', 'Miete einen Greifen von Gryth Thurden in Eisenschmiede.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50058 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50058, 'deDE', 'Löscht die Feuer', 'Der Angriff der Dunkeleisenzwerge hat uns überrascht. Ihre Saboteure haben viele unserer Flugzeuge, Dampfpanzer und Vorräte beschädigt. Wir konnten nur noch ein paar Bomber rechtzeitig starten.$B$BJeder Techniker, jeder Rekrut und jede Wache, die ich entbehren kann, versucht verzweifelt, die anderen zu reparieren und startklar zu machen. Unsere Infanterie kann nicht mehr lange ausharren. Nehmt diese Ausrüstung und helft bei der Löschung der brennenden Bomber.', 'Löscht 8 Feuer auf dem Flugplatz von Eisenschmiede mit Eurer Feuerwehrausrüstung.', '', 'Kehrt zu Unteroffizier Bahrum am Flugplatz von Eisenschmiede in Dun Morogh zurück.', 'Feuer gelöscht', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50059 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50059, 'deDE', 'Sammelt die Verteidiger', 'Während unsere Ingenieure dafür sorgen, dass die Bomber bald wieder fliegen, kämpfen unsere Wachen und Gebirgsjäger gegen die Eindringlinge.$B$BSeit Beginn des Einmarschs sind sie dort draußen, ohne Zeit für Schlaf oder Essen. Die Verteidiger von Eisenschmiede gehören zu den besten Azeroths, aber selbst sie haben Grenzen.$B$BNehmt dieses Horn und nutzt es in der Nähe der Truppen. Erinnert die Soldaten an das, wofür sie kämpfen und stärkt ihre Moral.', 'Trommelt 20 verbündete Truppen auf dem Flugplatz von Eisenschmiede zusammen.', '', 'Kehrt zu Unteroffizier Bahrum am Flugplatz von Eisenschmiede in Dun Morogh zurück.', 'Truppen zusammengetrommelt', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50060 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50060, 'deDE', 'Gegenschlag', 'Die Dunkeleisenzwerge greifen den Flugplatz von noch immer Eisenschmiede an! Ihre Truppen haben strategische Positionen eingenommen und bedrohen unsere Verteidigung. Wir müssen ihre Offensive zurückschlagen, bevor sie weiteren Schaden anrichten. Deine Aufgabe ist es, verschiedene Einheiten der Dunkeleisenzwerge auszuschalten und den Flugplatz zu sichern.', 'Eliminiert Truppen der Dunkeleisenzwerge.', '', 'Kehrt zu Unteroffizier Bahrum am Flugplatz von Eisenschmiede in Dun Morogh zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50061 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50061, 'deDE', 'Grimmaxts Ende', 'Dank eures Einsatzes ist der Angriff der Dunkeleisenzwerge ins Stocken geraten. General Grimmaxt, der Anführer der Dunkeleisenzwerge, versucht, seine Truppen aus ihren Reihen auf dem gefrorenen See im Süden anzutreiben.$B$BEr hat außerdem einen ihrer neuen Dunkeleisengolems und ruft seinen Soldaten Befehle zu. Wenn es ihm gelingt, sie zu motivieren, überrennen sie uns vielleicht doch noch. Das müsst Ihr verhindern, $N. Zerstört den Golem, der General Grimmaxt beschützt, dann ihn selbst. Er ist der Schlüssel zu unserem Sieg.', 'Tötet General Grimmaxt auf dem Flugplatz von Eisenschmiede.', '', 'Kehrt zu Unteroffizier Bahrum am Flugplatz von Eisenschmiede in Dun Morogh zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50062 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50062, 'deDE', 'Botschaft nach Eisenschmiede', 'Die Angriffe der Dunkeleisenzwerge auf den Flugplatz von Eisenschmiede wurden erfolgreich zurückgeschlagen, doch die Gefahr ist noch nicht gebannt. Der Große König Magni Bronzebart muss von diesen Ereignissen erfahren. Tragt diese Nachricht persönlich nach Eisenschmiede und übergebt sie Magni. Nur durch seine Führung können wir sicherstellen, dass unsere Verteidigung bereit ist, falls die Dunkeleisenzwerge erneut zuschlagen.', 'Überbringt die Nachrichten Magni Bronzebart in Eisenschmiede.', '', 'Überbringt die Nachrichten Magni Bronzebart in Eisenschmiede.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50063 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50063, 'deDE', 'Ein Funke Hoffnung', 'Die Lage ist kritisch, aber wir haben noch nicht verloren. Unteroffizier Bahrum koordiniert die Reparaturarbeiten und stellt sicher, dass unsere Verteidigung aufrechterhalten wird. Ich brauche, dass ihr ihm helft und ihm berichtet, wie der Stand der Dinge aussieht. Begebt euch zum südlichen Ende des Flugplatzes und sprecht mit ihm. Seine Führung ist jetzt entscheidend.', 'Meldet euch bei Unteroffizier Bahrum auf dem Flugplatz von Eisenschmiede, um weitere Anweisungen zu erhalten.', '', 'Meldet euch bei Unteroffizier Bahrum auf dem Flugplatz von Eisenschmiede, um weitere Anweisungen zu erhalten.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50064 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50064, 'deDE', 'Die Bedrohung durch Frostbann', 'Hört mir gut zu, $N. Ein gewaltiger Eisbär, bekannt als Frostbann, hat sich nordöstlich des Flugplatzes niedergelassen und gefährdet unsere Operationen. Dieses Biest terrorisiert die Region, greift Patrouillen an und zerstört wichtige Nachschubwege.$B$BWir brauchen einen mutigen Helden, der sich dieser Bedrohung stellt. Seid gewarnt: Frostbann ist kein gewöhnlicher Gegner. Seine eisige Haut ist nahezu undurchdringlich, und seine Klauen können Stahl mühelos durchtrennen. Bereitet euch gut vor und sammelt eure Kräfte, denn dieser Kampf wird in die Geschichtsbücher eingehen.', 'Tötet Frostbann den Eisbären', '', 'Kehrt zu Kommandant Steinbrecher am Flugplatz von Eisenschmiede in Dun Morogh zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50065 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50065, 'deDE', 'Die Splitter der Eissterne', 'Hey, du da! Ja, genau, du $C! Ich habe eine Theorie, dass die Glitzernden Frostkristalle im Frostflussbecken etwas mit der seltsamen Energie hier zu tun haben. Sie enthalten diese faszinierenden Eissternsplitter, und ich brauche einige davon für meine Experimente. Glaub mir, das wird DIE Erfindung des Jahrhunderts! Kannst du ein paar davon für mich einsammeln?', 'Sammle 8 Eissternsplitter (Ice Star Fragments) von den Glitzernden Frostkristallen im Frostflussbecken, Dun Morogh.', '', 'Kehrt zu Tüftler Glimmerzisch am Flugplatz von Eisenschmiede in Dun Morogh zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50066 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50066, 'deDE', 'Das Ende von Aqualon', 'Diese verrückte Theorie von mir über die Eissternsplitter hat ein riesiges Problem aufgedeckt: Aqualon, ein uralter Wasserelementar, ist im südwestlichen Teil des Frostflussbeckens aufgetaucht! Der Kerl hat wohl die Splitter gespürt und will sie für sich haben. Aber keine Sorge, ich habe dieses geniale Ding - den Frostgeborenen Kern. Er macht dem Typen ordentlich Schaden, aber er hat begrenzte Energie. Ich wette, du kriegst das hin, oder?', 'Besiege Aqualon im südwestlichen Teil des Frostflussbeckens mithilfe des Frostgeborenen Kerns.', '', 'Kehrt zu Tüftler Glimmerzisch am Flugplatz von Eisenschmiede in Dun Morogh zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50067 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50067, 'deDE', 'Die Üble Berührung', 'Wenn diese Spinnen mit Verderbnis in ihren Adern geboren werden, dann muss ihre Brutmutter die Ursache dieses Übels sein.$B$BLasst uns zum nördlichsten Ende der Höhle aufbrechen und Githyiss die Üble aufspüren.', 'Tötet Githyiss die Üble am nördlichen Ende der Schattenweberhöhle.', '', 'Kehrt zu Lunara Graufang im Schattenhain in Teldrassil zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50068 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50068, 'deDE', 'Natur als Vergeltung', 'Ich bin froh, dass Ihr bereit wart, für Euren Fehler Buße zu tun - das spricht sehr für Euren Charakter.$B$BIch habe eine weitere Aufgabe für Euch. Ich habe einige Zornwurzelsamen gesammelt. Sie sind ähnlich wie Dämonenkegel, aber weitaus potenter. Die Natur reagiert oft sehr negativ auf Dämonen, und diese Zornwurzelsamen bewirken eine stark konzentrierte Form dieser Vergeltung.$B$BNördlich von uns, nahe des Quellsees, gibt es Grellkin, die weit jenseits jeder Rettung sind. Benutzt die Zornwurzelsamen, um sie zu vertreiben, bevor sie eine ernstere Bedrohung werden.', 'Benutzt Zornwurzelsamen, um 12 Grellkin im Dämonenhain zu töten.', '', 'Kehrt zu Aralya Dämmerpfote in Dolanaar in Teldrassil zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50069 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50069, 'deDE', 'Elunes Rache', 'Während Teldrassil sich von der Verderbnis reinigt, die sein Wachstum all die Jahre verdorben hat, hat sich in der Nähe der Lichtung der Orakel, nördlich von hier, ein bösartiges Gewächs gebildet. Wir haben die Gelegenheit, vorzustoßen und die Quelle des Teufelsmooses zu beseitigen, das die Knarzpinien, Holzlinge und alles andere vergiftet hat.$B$BBeendet diese Verderbnis, $N.', 'Tötet den Zweig der Verderbnis im Norden von Teldrassil.', '', 'Kehrt zu Corithras Mondzorn in Dolanaar in Teldrassil zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50070 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50070, 'deDE', 'Die Heimat der Kaldorei', 'Es war eine unbestreitbare Ehre, Euch kennenzulernen. Ihr seid ein Beweis für die Adelhaftigkeit Eures Volkes.$B$BGeht jetzt und erledigt diese letzte Aufgabe. Bringt die Wasser zu Tyrande Wisperwind in Darnassus.', 'Bringt das gefüllte Gefäß zu Tyrande Wisperwind in Darnassus.', '', 'Bringt das gefüllte Gefäß zu Tyrande Wisperwind in Darnassus.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50071 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50071, 'deDE', 'Die Karawane zum Wegekreuz', '<Rocco überreicht Euch eine Schrotflinte.>$B$BIch habe mir erlaubt, Halgas Artillerie für Euch zu reinigen. Ich bin mir sicher, dass ich Euch nicht vor dem Rückstoß zu warnen brauche. Fragt mich einfach, wenn Ihr bereit seid, zum Wegekreuz aufzubrechen! Thork wartet dort auf Euch. Achtet darauf, dass Ihr ihm nicht pampig kommt.$B$BLasst Euch von nichts aufhalten, $N, und wenn Euch jemand zu nahe kommt, versetzt ihm einfach einen Stoß mit dem Kolbenende Eurer Schrotflinte. Macht uns stolz!', 'Sprecht mit Rocco Peitschschwinger in Fernwacht und begleitet seine Karawane den gesamten Weg zum Wegekreuz.', '', 'Kehrt zu Thork im Wegekreuz in Brachland zurück.', 'Karawanenzugkodo eskortiert', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50072 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50072, 'deDE', 'Auf zum Jägerhügel!', 'Schlechte Nachrichten haben uns aus Jägerhügel erreicht. Die Tauren-Siedlung ist in Gefahr, und sie haben dringend um Hilfe gebeten. Wir kennen noch nicht das volle Ausmaß der Bedrohung, aber wir wissen, dass der Grimmtotem-Stamm in der Gegend gesichtet wurde. Das kann nichts Gutes bedeuten.$B$BWir brauchen jemanden, der die Lage einschätzt und unseren Verbündeten beisteht. Geh nach Jägerhügel und melde dich bei Onatay. Sie wird dir sagen, was zu tun ist.', 'Meldet dich bei Onatay an der Höhenstraße im Brachland.', '', 'Meldet euch bei Onatay in Jägerhügel im Brachland.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50073 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50073, 'deDE', 'Säubert die Höhenstraße', 'Du bist gerade noch rechtzeitig gekommen, aber es bleibt keine Zeit zu verlieren! Die Grimmtotem greifen an und zeigen keine Anzeichen, dass sie aufhören werden. Sie kommen in Wellen, plündern unsere Vorräte, setzen unsere Gebäude in Brand und attackieren unser Volk.', 'Verteidige Jägerhügel, indem ihr die angreifenden Grimmtotem tötest.', '', 'Kehrt zu Onatay in Jägerhügel im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50074 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50074, 'deDE', 'Tierpflege', 'Die Grimmtotem greifen nicht nur unsere Krieger an - sie plündern auch unsere Vorräte. Sie haben einen Großteil unseres Getreides gestohlen, unsere wichtigste Nahrungsquelle für die kommenden Monate. Ohne dieses Getreide werden unsere Tiere verhungern.', 'Bergt 5 Säcke gestohlenes Korn von den Grimmtotem.', '', 'Kehrt zu Munada in Jägerhügel im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50075 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50075, 'deDE', 'Der Tod von Grundig Düsterwolke', 'Der Angriff der Grimmtotem geschieht nicht ohne Anführer. Grundig Düsterwolke, ein rücksichtsloser Häuptling, führt diese Überfälle an. Er ist für seine Grausamkeit bekannt und wird nicht aufhören, bis wir entweder vernichtet oder versklavt sind.$B$BWenn wir eine Chance haben wollen, die Grimmtotem endgültig zurückzudrängen, müssen wir der Schlange den Kopf abschlagen. Finde Grundig Düsterwolke und beende seine Schreckensherrschaft.', 'Tötet Grundig Düsterwolke.', '', 'Kehrt zu Onatay in Jägerhügel im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50076 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50076, 'deDE', 'Gefangene der Grimmtotem', 'Du gehörst nicht zu ihnen... Bitte, ich bin Ahuna Erdenrufer. Die Grimmtotem haben mich aus meiner Heimat verschleppt und in diesem Käfig eingesperrt. Ich weiß nicht, warum, aber ich fürchte, dass sie mich nicht mehr lange am Leben lassen werden.$B$BEiner ihrer Krieger muss den Schlüssel zu diesem Käfig haben. Ich flehe dich an, finde ihn und befreie mich, bevor es zu spät ist.', 'Findet den Käfigschlüssel, um Ahuna Erdenrufer zu befreien.', '', 'Kehrt zu Ahuna Erdenrufer an der Höhenstraße im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50077 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50077, 'deDE', 'Rückkehr zum Jägerhügel', '<Als du das Schloss öffnest, tritt Ahuna Erdenrufer vorsichtig aus dem Käfig. Nach Tagen der Gefangenschaft streckt sie ihre Glieder und sieht dich mit Dankbarkeit, aber auch mit Dringlichkeit an.>$B$BIch bin immer noch geschwächt von meiner Gefangenschaft, aber ich werde erst sicher sein, wenn ich nach Jägerhügel zurückkehre. Bitte, würdest du mich dorthin begleiten? Die Grimmtotem werden mich nicht so leicht ziehen lassen, und ich fürchte, dass ich nicht die Kraft habe, mich alleine zu verteidigen.', 'Eskortiert Ahuna Erdenrufer sicher zurück zum Jägerhügel.', 'Eskortiere Ahuna Erdenrufer zum Jägerhügel', 'Kehrt zu Onatay in Jägerhügel im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50078 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50078, 'deDE', 'Schlickbestie!', 'Ein monströses Wesen namens Schlickbestie.$B$BEs terrorisiert das Land, macht Jagd auf die Schwachen und vergiftet die Gewässer. Kein Jäger, der sich ihm allein gestellt hat, ist je zurückgekehrt. Wirst du derjenige sein, der seinem Schrecken ein Ende setzt?$B$BWenn ihr derjenige seid, der diese Monstrum zur Strecke bringt, werde ich euch mit Freuden meine Ware präsentieren.', 'Tötet Schlickbestie.', '', 'Kehrt zu Kurinika Seelensucher in Jägerhügel im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50079 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50079, 'deDE', 'Die Jagd auf Donnerzahn', 'Es gibt viele gefährliche Kreaturen im Brachland, aber nur wenige sind so gefürchtet wie Donnerzahn. Eine riesige Donnerechse mit Narben von unzähligen Kämpfen, klüger als die meisten Raubtiere. Er wandert zwischen der Brackigen Oase und der Blühenden Oase umher und jagt jeden, der sein Territorium betritt.$B$BViele haben versucht, ihn zu erlegen – keiner ist zurückgekehrt. Wenn du dir deiner Fähigkeiten sicher bist, dann finde Donnerzahn und bring ihn zur Strecke. Doch sei gewarnt: Dies wird kein gewöhnlicher Kampf.', 'Tötet Donnerzahn.', '', 'Kehrt zu Ramja Himmelsweber in Jägerhügel im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50080 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50080, 'deDE', 'Eine weitere Lieferung', 'Der Handel mit Kodos ist größer, als die meisten es glauben. Das Bilgewasserkartell steckt tief in diesem Geschäft - sie finanzieren nicht nur den Transport, sondern auch die Kodos selbst. Ihre Verträge sind überall, und wir Tauren haben wenig Kontrolle über die Preise oder wer letztendlich von diesem Handel profitiert.$B$BDieser Karawanenzugkodo muss sicher nach Camp Taurajo gebracht werden, wo Jorn Himmelsdeuter ihn in Empfang nehmen wird. Es ist eine einfache, aber gefährliche Reise - Straßenräuber, Raubtiere und sogar rivalisierende Händler könnten Interesse an unserem Transport haben.$B$BSorge dafür, dass der Kodo heil ankommt. Wenn das Bilgewasserkartell uns schon kontrolliert, sollten wir zumindest sicherstellen, dass ihre Investition nicht verloren geht.', 'Sprecht mit Thork im Wegekreuz und begleitet seine Karawane den gesamten Weg nach Camp Taurajo.', '', 'Kehrt zu Jorn Himmelsdeuter im Camp Taurajo in Brachland zurück.', 'Karawanenzugkodo eskortiert', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50081 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50081, 'deDE', 'Der Stolz des Löwen', 'Wenn Ihr Rache an den Eindringlingen der Grimmtotem nehmen wollt, seid Ihr gut beraten, die gefährlichsten Raubtiere im Brachland zu beobachten.$B$BDie Löwen, die sich durch das hohe Gras schleichen, sind gerissen, gut getarnt und mächtig. Nehmt Euch an ihnen ein Beispiel und Ihr werdet verstehen, welche Art Krieg wir führen müssen.$B$BJagt die Savannenmatriarchinnen und die Savannenpatriarchen. Dann kehrt zu mir zurück und wir unterhalten uns darüber, was Ihr gelernt habt. Ihr werdet sie oft im Schatten großer Bäume antreffen.', 'Tötet Savannenmatriarchinnen und Savannenpatriarchen im Brachland.', '', 'Kehrt zu Munada in Jägerhügel im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50082 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50082, 'deDE', 'Die letzte Welle Überlebender', 'Ein Schiff mit Nachtelfen versuchte, dem Sturm zu entkommen, doch das Meer zeigte keine Gnade. Die tobenden Wellen zerrissen es, und nun liegt das Wrack über die Küste verstreut. Viele wurden von den Fluten verschlungen, doch einige Überlebende wurden an den Strand gespült.$B$BWir müssen schnell handeln. Gehe die Küste nach Norden entlang und finde so viele Überlebende wie möglich.', 'Verwende den Samen der Erneuerung auf den Überlebenden von Auberdine, um sie zu heilen.  ', '', 'Kehrt zu Dentaria Silbertal in Auberdine in Dunkelküste zurück.', 'Überlebende von Auberdine gerettet', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50083 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50083, 'deDE', 'Gefahr aus dem Wasser', 'Einige der Überlebenden vom letzten Schiff, das Auberdine verlassen hat, versuchen immer noch, die Küste hinauf in Sicherheit zu gelangen. Erst vor einer Stunde haben wir erfahren, dass sie unterwegs sind. Ich wünschte, wir hätten mehr Zeit zur Vorbereitung gehabt...$B$BDie Küste nördlich von uns wimmelt von Üblen Sprühern – wilden Wasserelementaren. Wenn die Überlebenden eine Chance haben sollen, Auberdine zu erreichen, müssen wir sie beseitigen. Brich sofort auf und töte jedes Elementar, das du siehst.  ', 'Töte 6 Widerwärtige Elementare und 12 Widerwärtige Gischt.', '', 'Kehrt zu Waldläuferin Vaelina Sonnenhauch in Auberdine in Dunkelküste zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50084 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50084, 'deDE', 'Dröhnkiste 413', 'Seid gegrüßt!$B$BIch helfe Tharnariun dabei, herauszufinden, was die Tierwelt in diesem Gebiet verderbt. Ich habe einige Maschinen aufgestellt, die Tierproben und mögliche Verderbnisquellen analysieren können. Wenn Ihr mir gute Proben bringt, kommen wir diesem Übel schnell auf die Spur.$B$BDie erste Maschine ist Dröhnbox 413. Ich habe sie am Strand nördlich der Stadt aufgestellt. Ich brauche Fleischproben der verdorbenen Gezeitenkrabbler aus der Umgebung, damit ich sie analysieren kann.  ', 'Sammelt 4 Proben verderbtes Gezeitenkrabblerfleisch und bringt es zur Analyse zur Dröhnkiste 413.', '', 'Kehrt zu Hexknall Kurbeldreh in Auberdine in Dunkelküste zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50085 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50085, 'deDE', 'Über Geschmack lässt sich nicht streiten', '<Die Maschine surrt einige Momente lang, bevor Wizbangs Stimme durch das Rauschen dringt.>$B$BInteressant! Die Quelle dieser Verderbnis stammt eindeutig von etwas Äußerem – wenig überraschend –, aber es sieht so aus, als hätten sie etwas Verdorbenes gefressen. Ein großes Säugetier vermutlich. Höchstwahrscheinlich ein Bär.$B$BSucht am Strand nach einem verendeten Bären. Etwas östlich von dort wäre sinnvoll, auf der landseitigen Seite des Strandes. Bringt mir die Probe zurück in die Stadt.  ', 'Bergt eine Probe von einem verdorbenen Bären im Wald in der Nähe von Brummbox 413.  ', '', 'Kehrt zu Hexknall Kurbeldreh in Auberdine in Dunkelküste zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50086 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50086, 'deDE', 'Dröhnkiste 723', 'Es gibt ein paar andere Tierarten, bei denen wir eine Infektion bestätigt haben. Eigentlich sind das gute Nachrichten – wenn die Krabben sich bei den Bären angesteckt haben, dann bedeutet das hoffentlich, dass alles auf eine einzige Quelle zurückzuführen ist. Wir müssen uns jetzt auf diese Bären konzentrieren.$B$BDröhnbox 723 befindet sich bei den Wasserfällen im Osten. Es wird eine unangenehme Aufgabe, aber sie ist notwendig. Ich brauche frische Bäreninnereien, die Ihr für die Analyse in die Dröhnbox geben müsst. Ihr findet Dröhnbox 723 östlich von Dröhnbox 413, direkt neben einem Höhleneingang. Sobald ich die Informationen habe, sehen wir weiter.  ', 'Sammelt 6 Proben verderbte Eingeweide eines Distelbären und bringt sie für eine Analyse zur Dröhnkiste 723.', '', 'Kehrt zur Dröhnkiste 723 bei den Klippenquellfällen nahe dem Höhleneingang zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50087 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50087, 'deDE', 'Der Ursprung der Verderbnis', 'Jetzt weiß ich, wer hinter diesem Übel steckt. Zenn Faulhuf.$B$BEr ist von Teldrassil herübergekommen, nachdem er dort für seine abscheulichen magischen Experimente verjagt wurde. Die Art von Verderbnis, die er nutzt, ist unter meinem Volk nicht willkommen. Es scheint, als wäre er diesmal noch einen Schritt weiter gegangen – vielleicht aus Rache.$B$BZenn hat bereits zu viele Chancen bekommen. Es ist an der Zeit, diese Bedrohung für alles Natürliche ein für alle Mal zu beseitigen. Tötet Zenn und seine Schergen und bringt mir dann die gute Nachricht.  ', 'Tötet Zenn Faulhuf, 6 Üble Grells und 6 Üble Verderber in der Schattenwurzhöhle.  ', '', 'Kehrt zu Waldläuferin Vaelina Sonnenhauch in Auberdine in Dunkelküste zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50088 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50088, 'deDE', 'Heilung im Dunkeln', '<Unverständliches Gebrabbel kommt aus der Maschine, bevor Ihr Wizbangs Stimme wieder erkennt.>$B$BDämmerpelzratten! Sie haben Dämmerpelzratten gefressen! Diese kleinen Biester kommen nur an einem einzigen Ort vor – einer Höhle in der Felswand im Osten. Findet einen Pfad nach Nordosten und sucht nach einer Öffnung unterhalb des Wasserfalls.$B$BDiese Ratten fressen entweder etwas Verdorbenes, oder jemand verbreitet absichtlich eine verderbende Substanz durch sie. In jedem Fall bringt mir mehrere Proben davon mit!  ', 'Erhaltet 6 Proben widerlicher Schleim von den Kreaturen in der Schattenwurzhöhle.', '', 'Kehrt zu Hexknall Kurbeldreh in Auberdine in Dunkelküste zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50089 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50089, 'deDE', 'Die letzte Flamme von Ameth\'Aran', 'In Zeiten wie diesen sind wir gezwungen, innezuhalten und über unsere Lebensweise nachzudenken. Wir sind nun sterblich, und so sehr wir uns auch das Gegenteil wünschen, wir müssen unseren neuen Platz in dieser Welt akzeptieren.$B$BAmeth\'Aran, die Straße hinunter nach Süden, war einst eine stolze Stadt unserer Ahnen. Jetzt wird sie von ihren verfluchten Geistern bewohnt. Wir hofften, dass auch sie, wie wir selbst, erlöst werden könnten, doch wir haben sie viel zu lange in ihrem Leid gelassen. Es ist an der Zeit, die letzte Flamme von Ameth\'Aran zu löschen.  ', 'Löscht die letzte Flamme von Ameth\'Aran.', '', 'Kehrt zu Waldläuferin Vaelina Sonnenhauch in Auberdine in Dunkelküste zurück. ', 'Flamme von Ameth\'Aran gelöscht', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50090 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50090, 'deDE', 'Schmerz im Überfluss', 'Die Apothekarium-Aufseher haben uns neue Vorschriften auferlegt… als ob wir uns jemals von ein paar Regeln aufhalten lassen würden!"$B$BSie erwarten, dass wir mit dieser abgeschwächten Version unserer Toxine arbeiten - eine reine Zeitverschwendung, wenn du mich fragst.$B$B"Wenn ich das Zeug irgendwie brauchbar machen soll, brauche ich große Mengen an wildem Verdammnisunkraut. Du findest es überall im Silberwald. Bring mir so viel du kannst!', 'Sammelt 8-mal Wildes Verdammniskraut.', '', 'Kehrt zu Hauptmann Aldrin nahe des Grabmals im Silberwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50091 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50091, 'deDE', 'Schatten in der Dunkelheit', 'Sie dachten, sie könnten mich einfach aus dem Schatten heraus angreifen... als ob ich das nicht kommen gesehen hätte!$B$BDiese Späher der Dunkelpfoten sind überall im Silberwald verteilt. Sie beobachten, warten und schlagen zu, wenn sie glauben, dass wir schwach sind.$B$BZeig ihnen, dass wir nicht schwach sind, $C. Jage diese Schatten aus dem Wald und bring mir ihre Zahlungen in Blut!', 'Tötet 5 Späher der Dunkelpfoten.', '', 'Kehrt zu Hauptmann Aldrin nahe des Grabmals im Silberwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50092 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50092, 'deDE', 'Beutejäger und Runenwirker', 'Unsere Späher haben Worgen nahe der Küste entdeckt. Es sieht so aus, als würden sie sich dort sammeln, vielleicht für eine größere Offensive.$B$BDie Jäger der Dunkelpfoten durchstreifen das Land, auf der Suche nach Schwächen in unseren Verteidigungslinien. Die Magier der Dunkelpfoten... nun, ihre Zauber sind keine bloßen Tricks. Sie beschwören dunkle Energien, die unsere Truppen schwächen.$B$BLass sie nicht erstarken. Töte ihre Jäger und ihre Magier, bevor sie uns zu ihrer Beute machen!', 'Tötet 6 Jäger der Dunkelpfoten und 4 Magier der Dunkelpfoten.', '', 'Kehrt zu Hauptmann Aldrin nahe des Grabmals im Silberwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50093 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50093, 'deDE', 'Tiefer in die Dunkelheit', 'Die Netzhuschermatriarchin ist in der Nähe! Ihr könnt der Bedrohung durch die Netzhuscher ein Ende bereiten, indem Ihr sie erlegt!$B$BFindet sie am Ende der Höhle im Huschdunkel und vernichtet sie!', 'Tötet die Netzhuschermatriarchin.', '', 'Kehrt zu Apotheker Renferrel beim Grabmal im Silberwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50094 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50094, 'deDE', 'Die Straße aufräumen', 'Jedes Mal, wenn wir versuchen, Vorräte durch den Silberwald zu transportieren, schlagen diese verfluchten Worgen aus den Schatten zu. Karawanen werden umgestürzt, Wachen abgeschlachtet, und unsere Waren liegen verstreut auf der Straße wie Blätter im Wind.$B$BWir können es uns nicht leisten, weitere Lieferungen zu verlieren. Durchsuche die Straße nach verlorenen Karawanenkisten und bring zurück, was noch brauchbar ist. Falls die Worgen nicht alles gestohlen haben, können wir vielleicht einige unserer Verluste ausgleichen.', 'Sammelt 7 Karawanengüter.', '', 'Kehrt zu Hauptmann Aldrin nahe des Grabmals im Silberwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50095 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50095, 'deDE', 'Macht ihnen die Hölle heiß!', 'Wir haben die Angriffe bis zu ihrem Anführer zurückverfolgt - Rudelführer Garrik. Er und sein Rudel durchstreifen die Straßen und warten auf den perfekten Moment zum Zuschlagen. Sie nehmen nicht nur, was sie brauchen, sie schlachten jeden ab, der sich ihnen in den Weg stellt.$B$BDas reicht. Finde Garrik und schalte ihn aus. Diese Worgen sollen wissen, dass wir nicht ihre Beute sind.', 'Tötet Rudelführer Garrik.', '', 'Kehrt zu Hauptmann Aldrin nahe des Grabmals im Silberwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50096 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50096, 'deDE', 'Keine Gnade', 'Die Gilneer sind in die Enge getrieben. Ihre ach so kostbare Stadt liegt hinter dieser verfluchten Mauer, doch sie wissen, dass sie dort keine Zuflucht finden werden. Stattdessen versuchen sie, in Allianzgebiete zu fliehen, in der Hoffnung auf Rettung.$B$BDas können wir nicht zulassen.$B$BDiese Flüchtlinge mögen jetzt schwach sein, doch wenn sie ihre Verbündeten erreichen, kehren sie bewaffnet und kampfbereit zurück. Jage sie, bevor sie uns entkommen. Ihre Leichen sollen eine Warnung sein – niemand entkommt der Horde.', 'Eliminiert Flüchtlinge aus Gilneas nahe dem Graumähnenwall.', '', 'Kehrt zu Hauptmann Aldrin nahe des Grabmals im Silberwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50097 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50097, 'deDE', 'Dreckige Pfoten', 'Diese verfluchten Splittersteinkobolde schleichen sich nach Thelsamar und stehlen unsere Vorräte! Sie haben Essen, Waffen... sogar unser Schießpulver mitgenommen!$B$BWir brauchen diese Vorräte zurück, bevor sie tief in ihre Minen geschleppt werden. Durchsuche die Straße und sammle alles ein, was du finden kannst, $C!  ', 'Bergt 6 gestohlene Vorräte von den Troggs entlang der Straße von Thelsamar.  ', '', 'Kehrt zu Gebirgsjäger Thorgar nach Thelsamar in Loch Modan zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50098 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50098, 'deDE', 'Die Straßen sichern', 'Die Straßen nach Thelsamar werden von Tag zu Tag gefährlicher. Eine Bande von Straßenräubern überfällt Reisende, stiehlt Vorräte und greift sogar unsere Karawanen an!$B$BDas endet jetzt, $C. Jage sie und zeige ihnen, wer hier das Sagen hat!  ', 'Tötet 9 Straßenräuber der Splittersteintroggs entlang der Straße von Thelsamar.  ', '', 'Kehrt zu Gebirgsjäger Thorgar nach Thelsamar in Loch Modan zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50099 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50099, 'deDE', 'Die Trogg-Bedrohung', 'Diese abscheulichen Troggs tauchen in immer größeren Zahlen aus den Bergen auf und werden zu einer echten Bedrohung für unsere Siedlungen.$B$BWenn wir uns nicht um sie kümmern, werden sie Thelsamar bald überrennen. Geh hinaus und reduziere ihre Zahl!  ', 'Tötet Splittersteintroggs südöstlich von Thelsamar.  ', '', 'Kehrt zu Gebirgsjäger Thorgar nach Thelsamar in Loch Modan zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50100 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50100, 'deDE', 'Jagdsaison', 'Unsere Jäger kommen kaum noch mit der wachsenden Anzahl an Wölfen und Bären zurecht. Die Bestien haben begonnen, unsere Lager und sogar unsere Außenposten anzugreifen!$B$BWir brauchen dich, um ihre Zahl zu verringern, bevor sie zu einer ernsten Gefahr werden. Bring ihre Pelze zurück – wir lassen nichts ungenutzt!$B$BDie meisten von ihnen findest du östlich des großen Sees.  ', 'Tötet 6 Silbermähnenwölfe und 4 Schwarzbären-Patriarchen.  ', '', 'Kehrt zu Jagdmeister Grivar nach Thelsamar in Loch Modan zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50101 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50101, 'deDE', 'Die Murloc-Plage', 'Ich weiß nicht, woher diese Murlocs gekommen sind, aber sie haben sich am Ufer breitgemacht. Anfangs hielten sie sich zurück, doch inzwischen greifen sie unsere Fischerboote an!$B$BDas können wir nicht zulassen. Vernichte sie, bevor sie sich den ganzen See unter den Nagel reißen!  ', 'Tötet 15 Murlocs entlang des Ufers von Loch Modan.  ', '', 'Kehrt zu Jagdmeister Grivar nach Thelsamar in Loch Modan zurück.  ', 'Loch Murlocs getötet', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50102 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50102, 'deDE', 'Die Murloc-Plünderung', 'Diese verdammten Murlocs überfallen unsere Fischerboote! Nachts schleichen sie sich an, kippen die Boote um und stehlen die Fische direkt aus unseren Netzen. Jetzt horten sie Fässer voller gestohlener Fische am Ufer und stopfen sich voll, während unser Volk hungert!$B$BWir müssen diese Vorräte zurückholen, bevor sie in Murloc-Mägen verrotten. Schleich dich in ihre Lager an der Südküste und rette, was noch zu retten ist!  ', 'Bergt 8 Fässer mit Fisch aus den Lagern der Murlocs.  ', '', 'Kehrt zu Jagdmeister Grivar nach Thelsamar in Loch Modan zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50103 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50103, 'deDE', 'Der Anführer der Troggs', 'Wir haben einen gefährlichen Anführer unter den Troggs identifiziert: Gorlog der Steinschmetterer. Er ist schlauer als die anderen, und wir vermuten, dass er hinter diesen Angriffen steckt.$B$BWenn wir ihn ausschalten, wird ihre Truppe ins Chaos stürzen. Aber sei vorsichtig, $C – er wird sich nicht kampflos ergeben! Such dir ein paar Freunde!  ', 'Tötet Gorlog den Steinschmetterer in der Silberstrommine nahe Ironbands Ausgrabung.  ', '', 'Kehrt zu Gebirgsjäger Thorgar nach Thelsamar in Loch Modan zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50104 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50104, 'deDE', 'Die Höhlen säubern', 'Die Troggs sind keine hirnlosen Schläger – sie organisieren sich! Unsere Späher berichten, dass Gorlog, ihr selbsternannter Kriegsherr, einen noch größeren Angriff plant.$B$BWir können nicht darauf warten, dass sie zuerst zuschlagen. Geh in ihre Höhlen und dünne ihre Reihen aus, bevor sie eine groß angelegte Offensive starten können. Zerstöre dabei auch ihre Vorratskisten – ich will nicht, dass sie wohlgenährt und gut bewaffnet in den Kampf ziehen!  ', 'Tötet 6 Tiefenschlachter der Splittersteintroggs in der Silberstrommine nahe Ironbands Ausgrabung.  ', '', 'Kehrt zu Gebirgsjäger Thorgar nach Thelsamar in Loch Modan zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50105 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50105, 'deDE', 'Ein gefangener Jäger', 'Ihr da! Bitte helft mir! Diese verdammten Troggs haben mich überfallen, als ich Wölfen nachstellte. Sie haben mich bewusstlos geschlagen und in dieses stinkende Loch geschleppt. Ich glaube, sie wollten mich zum Abendessen servieren!$B$BIch kann noch kämpfen, aber ich bin nicht in der Verfassung, es mit ihnen alleine aufzunehmen. Wenn ihr mich hier rausholt, verspreche ich, euch nicht aufzuhalten. Aber haltet sie mir bitte vom Leib, ja?$B$BSobald wir hier draußen sind, muss ich mich bei Bergjäger Thorgar in Thelsamar melden. Er muss erfahren, was die Troggs vorhaben.  ', 'Eskortiert den gefangenen Jäger Edrin sicher aus der Silberstrommine.  ', 'Eskortiere Jäger Edrin aus der Silberstrommine', 'Kehrt zu Gebirgsjäger Thorgar nach Thelsamar in Loch Modan zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50106 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50106, 'deDE', 'Verloren auf dem Grund des Lochs', 'Ach, $C, jetzt habe ich es wirklich vermasselt! Als wir am Damm gearbeitet haben, habe ich meinen Familienring verloren – ein wertvolles Erbstück, das von Prospektor zu Prospektor weitergegeben wird!$B$BIch habe versucht, ihn selbst zu bergen, aber da unten ist etwas. Riesige Fische mit dolchähnlichen Zähnen, und schlimmer noch – ich schwöre, da lauert etwas in den Schatten.$B$BWenn du den Mut hast, tauche hinab und finde meinen Ring. Aber sei vorsichtig. Sonst wird nicht nur mein Ring auf dem Grund des Lochs verschwinden!  ', 'Bergt Ironbands verlorenen Ring vom Grund des Lochs Modan.  ', '', 'Kehrt zu Eisenband bei Eisenbands Ausgrabungsstätte in Loch Modan zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50107 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50107, 'deDE', 'Hunger und Not', 'Wir wurden von unseren Höfen vertrieben, unser Land niedergebrannt... und Sturmwind tut nichts!$B$BUnsere Kinder hungern, unsere Alten sterben im Dreck. Und dennoch nennen sie uns nichts weiter als Bettler und Verbrecher!$B$BWenn Sturmwind uns nicht helfen will, dann vielleicht Ihr, $C? Jagd die wilden Bestien in der Umgebung und bringt so viel Fleisch zurück, wie Ihr finden könnt. Wenigstens heute Nacht werden unsere Leute nicht mit leerem Magen schlafen müssen.', 'Sammelt 8 Portionen Wildfleisch.', '', 'Kehre zu Bauer Jakob in Späherkuppe in Westfall zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50108 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50108, 'deDE', 'Verzweifelte Maßnahmen', 'Fleisch hilft für einen Tag, aber wir brauchen mehr als nur eine einzige Mahlzeit...$B$BDie Felder der Bauern... es liegt noch Getreide auf den verlassenen Äckern. Dieses Getreide gehört niemandem mehr, aber wenn wir es bergen können, haben wir vielleicht eine Chance, etwas länger zu überleben.$B$BDoch die Felder sind von Feinden und Defias überrannt. Ihr müsst vorsichtig sein, wenn Ihr nach dem Getreide sucht.', 'Sammelt 6 Säcke Getreide.', '', 'Kehre zu Bauer Jakob in Späherkuppe in Westfall zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50109 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50109, 'deDE', 'Hoffnung für die Hoffnungslosen', 'Wir können nicht weiter so leben. Sturmwind muss uns helfen! Nicht alle von ihnen sind herzlos... oder doch?$B$BIch habe Gerüchte gehört, dass ein Beamter in Sturmwind für Hilfsmaßnahmen zuständig ist.$B$BWenn er versteht, wie schlimm es um uns steht, kann er vielleicht die Adligen dazu bringen zu handeln.$B$BBitte, sprecht mit ihm. Lasst ihn erkennen, dass wir keine Verbrecher sind – wir sind Menschen, die Hilfe brauchen!', 'Sprecht mit Minister Dunst in Sturmwind und bittet ihn um Hilfe für die Flüchtlinge.', '', 'Sprich mit Minister Dunst in Späherkuppe, um Hilfe für die Flüchtlinge zu erbitten.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50110 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50110, 'deDE', 'Schweigen oder Tod', 'Ah ja... die sogenannten ‚Obdachlosen‘. Sie sind eine Krankheit in diesem Land! Verbrecher, die sich als Opfer ausgeben!$B$BDer König hat keine Zeit für Bettler. Westfall ist verloren, und es gibt weitaus wichtigere Angelegenheiten zu regeln.$B$BWenn Ihr wirklich helfen wollt, dann macht diesen Rebellen ein Ende – mit allen Mitteln!', 'Kehrt zu Bauer Jakob in Späherkuppe in Westfall zurück.', '', 'Kehre zu Bauer Jakob in Späherkuppe in Westfall zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50111 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50111, 'deDE', 'Feuer der Rebellion', 'Die Rebellen haben bereits zugeschlagen, $C. Während Ihr in Sturmwind wart, haben sie einen Angriff auf Späherkuppe gestartet!$B$BIn der Nacht sind Aufständische an unseren Verteidigern vorbeigeschlüpft und haben mehrere Vorratslager sowie Wohnhäuser in Brand gesetzt.$B$BDer Schaden ist schwer, und wenn wir nicht schnell handeln, könnte die gesamte Siedlung in Flammen aufgehen!$B$BNehmt einen Wassereimer bei den nahegelegenen Fässern und löscht die Brände, bevor es zu spät ist!', 'Nutzt den Wassereimer aus den nahegelegenen Wasserfässern, um 8 Brände zu löschen.', '', 'Kehre zu Hauptmann Reginald in Späherkuppe in Westfall zurück.  ', 'Feuer gelöscht', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50112 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50112, 'deDE', 'Niederschlagung des Aufstands', 'Die Rebellen greifen in voller Stärke an!$B$BBewaffnete Aufständische strömen aus allen Richtungen herbei und massakrieren unsere Wachen.$B$BWenn wir nicht kämpfen, wird Späherkuppe fallen!$B$BGreift zu den Waffen, $C. Tötet jeden Rebellen, den Ihr seht!', 'Verteidigt euch gegen den Angriff der Rebellen.', '', 'Kehre zu Hauptmann Reginald in Späherkuppe in Westfall zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50113 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50113, 'deDE', 'Der Funke der Rebellion', 'Die verzweifelten Bauern werden zu Rebellen. Wir müssen herausfinden, wo ihre Anführer sich verstecken.$B$BIch habe Berichte über geheime Treffen in alten Ruinen östlich von hier erhalten. Wir müssen wissen, was sie planen.$B$BFindet einen Weg, um das Treffen zu belauschen.', 'Spioniert das geheime Treffen östlich von Späherkuppe aus.', '', 'Kehre zu Hauptmann Reginald in Späherkuppe in Westfall zurück.  ', 'Das geheime Treffen belauscht', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50114 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50114, 'deDE', 'Zerschlagung der Aufständischen', 'Die Zeit des Wartens ist vorbei. Diese Rebellen haben ihren Weg gewählt, und wir können nicht zulassen, dass sie sich gegen Sturmwind erheben.$B$BWir haben mehrere Lager der Aufständischen in ganz Westfall identifiziert.$B$BSchlagen wir jetzt zu, können wir ihre Streitkräfte zerschlagen, bevor sie sich weiter organisieren!$B$BBegebt Euch dorthin und eliminiert die Aufständischen, bevor es zu spät ist, $C.', 'Tötet 6 Schurkische Bauer und 6 Soldaten der Westfallrebellen.', '', 'Kehre zu Hauptmann Reginald in Späherkuppe in Westfall zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50115 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50115, 'deDE', 'Das Ende des Aufruhrs', 'Wir haben Berichte erhalten, dass sich die Anführer des Aufstands in Mondbruch verstecken. Sie sammeln die Bauern um sich und machen sie zu Soldaten.$B$BDas dürfen wir nicht zulassen.$B$BTöten wir die Anführer, wird die Rebellion zusammenbrechen, bevor sie Elwynn oder das Rotkammgebirge erreicht.$B$BFindet ihre Kommandanten und schaltet sie aus. Keine Gnade, $C.', 'Tötet 3 Anführer der Westfallrebellen.', '', 'Kehre zu Hauptmann Reginald in Späherkuppe in Westfall zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50116 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50116, 'deDE', 'GESUCHT: Mechanische Bedrohung', 'GESUCHT!$B$BDie alten Erntergolems haben einst die Felder bestellt, doch nun sind sie eine Bedrohung!$B$BEtwas – oder jemand – hat sie reaktiviert, und nun greifen sie alles an, was sich bewegt.$B$BEs gibt Berichte über einen gigantischen Golem, den Überladenen Ernter, der durch die Felder wütet und alles zerstört, was ihm im Weg steht.$B$BDiese Maschine muss aufgehalten werden, bevor sie Späherkuppe erreicht!$B$BEine großzügige Belohnung erwartet jene, die mutig genug sind, sie zu vernichten!', 'Besiegt den überladenen Ernter auf den südlichen Feldern von Westfall.', '', 'Kehre zu Marschall Gryan Starkmantel in Späherkuppe in Westfall zurück.  ', 'Überladener Ernter zerstört', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50117 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50117, 'deDE', 'GESUCHT: Elementare Bedrohung', 'GESUCHT!$B$BDer Boden bebt, und die Menschen zittern.$B$BWas wir für Erdbeben hielten, stellte sich als etwas weitaus Schlimmeres heraus – ein gewaltiges Erdwüter-Elementar namens Felsszorn ist aus den Tiefen aufgestiegen!$B$BEr zerschmettert Felsen, zerstört Gebäude und vernichtet alles auf seinem Weg.$B$BSollte er Späherkuppe erreichen, wird nichts mehr übrig bleiben!$B$BEine Belohnung wird jedem Helden – oder jeder Heldengruppe – geboten, die dieses Ungetüm bezwingt, bevor es uns alle begräbt!', 'Besiegt Felsszorn, der im nordwestlichen Teil von Westfall patrouilliert.', '', 'Kehre zu Marschall Gryan Starkmantel in Späherkuppe in Westfall zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50118 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50118, 'deDE', 'GESUCHT: Übernatürliche Bedrohung', 'GESUCHT!$B$BSeit Jahren flüstert man von dem Seelenlosen – ein Name, den man einst als Legende abtat.$B$BDoch nun werden die Schatten in Mondbruch unruhig. Gestalten huschen am Rande des Blickfeldes, und jene, die das verlassene Bauernhaus betreten, kehren nicht zurück.$B$BEinige sagen, sie hören schwaches Lachen im Wind – andere schwören, das Haus selbst atme.$B$BDoch die Wahrheit ist noch weitaus furchteinflößender.$B$BDer Seelenlose weilt nicht in unserer Welt, sondern in einem Ort jenseits – einer verfluchten Spiegelung von Mondbruch, einem Reich, in dem die Toten flüstern und die Realität zerbricht: Der Schleier des Mournings.$B$BNur jene, die das Siegel brechen, können in diese verlorene Welt eintreten.$B$BDas lange verschollene Artefakt, das Schleierbrecher-Amulett, birgt den Schlüssel. Wird es in Mondbruch benutzt, zerreißt es die Grenze zwischen den Welten und enthüllt, was verborgen bleiben sollte.$B$BWagt Ihr es, das Reich des Seelenlosen zu betreten?$B$BDie Stadt fleht um Hilfe. Verbannt ihn, bevor seine Verderbnis in unsere Welt sickert.$B$BEine Belohnung erwartet jene, die zurückkehren... falls sie zurückkehren.', 'Besiegt den Seelenlosen in Mondbruch in Westfall.', '', 'Kehre zu Marschall Gryan Starkmantel in Späherkuppe in Westfall zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50119 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50119, 'deDE', 'Hüter der Flamme  ', 'Das Feuer der Erde ist heilig. Es gibt uns Wärme, schmiedet unsere Waffen und schützt unsere Ahnen. Doch seine Macht muss bewahrt werden.$B$BHier, in den Bergen der Steinkrallengebirge, sickert ein seltsamer Schlamm aus den Felsen - durchdrungen von ruheloser Energie. Ich spüre sein Ungleichgewicht, ein Zeichen dafür, dass etwas Unnatürliches erwacht.$B$BSammelt diesen Schluchtschleim für mich. Ich muss ihn reinigen, bevor die Verderbnis sich weiter ausbreitet.  ', 'Sammelt 6 Schluchtenschlamm in den Steinkrallengebirgen.', '', 'Kehrt zu Scharfseher Donnerhuf in Sonnenfels in den Steinkrallengebirgen zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50120 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50120, 'deDE', 'Traum von einer besseren Zukunft  ', 'Hör zu, Kumpel! Ich hatte eine Vision. Ein Imperium der Baumfällerei, der Geldmacherei, der ultimativen Effizienz! Nur ein Problem... mein Schredder? Ja, der ist hinüber. Total durchgebrannt. Also, er ist explodiert und hätte mir fast das Gesicht weggesprengt-mäßig hinüber.$B$BAber hey, ich gebe nicht auf! Ich brauche nur ein paar hochwertige Ersatzteile. Zum Glück gibt es hier genug Schrott, den wir recyclen können!$B$BBeschaff mir einen BD-816 Kriegsapparat von einer dieser kaputten Kriegsmaschinen, plus ein Mechanisiertes Feuer, Eis und Luft, um das Ding wieder anzutreiben. Einfacher Job, oder? Also los, Zeit ist Geld!  ', 'Bergt den BD-816 Kriegsapparat, Mechanisiertes Feuer, Mechanisiertes Eis und Mechanisierte Luft.', '', 'Kehrt zu Grizznik Zahnrat an der Scherwindklippe in den Steinkrallengebirgen zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50121 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50121, 'deDE', 'Herr Rosie', 'Siehst du diese großen, blättrigen Ärgernisse da draußen? Steinkrallenbäume. Sie stehen einfach nur da, nehmen Platz weg, versperren die Aussicht - bestes Bauland, wenn du mich fragst!$B$BZum Glück hab ich da was: Rosie! Die neueste und beste Hochgeschwindigkeits-Technologie im Baumfällen!$B$BAlso steig ein, starte sie und leg los!  ', 'Nutzt Rosie, um 6 Steinkrallenbäume zu fällen.', '', 'Kehrt zu Grizznik Zahnrat an der Scherwindklippe in den Steinkrallengebirgen zurück.  ', 'Steinkrallenbaum gefällt', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50122 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50122, 'deDE', 'HÜTET EUCH VOR FELSKIEFER!  ', 'Der Felskesselsee ist SPERRZONE!$B$BDas wilde Biest namens Felskiefer hat den See in eine Todesfalle verwandelt. Solltet ihr Felskiefer erledigen, nehmt seinen riesigen Zahn als Trophäe und bringt ihn zu Scharfseher Erdensang, um eure Belohnung zu erhalten.  ', 'Tötet Felskiefer und nehmt Felskiefers riesigen Zahn.', '', 'Kehrt zu Scharfseher Erdensang in Sonnenfels in den Steinkrallengebirgen zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50123 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50123, 'deDE', 'Erinnerungen einfangen  ', 'Vor langer Zeit errichteten die Hochgeborenen gewaltige Hallen in diesen Ruinen. Nun bleiben nur noch Staub und zerbrochener Stein. Doch ihre Vergangenheit verweilt hier noch immer.$B$BDie Geister flüstern von etwas, das unter den Trümmern begraben liegt - Artefakte, Relikte von Eldre’Thar. Fragmente der Geschichte, die darauf warten, entdeckt zu werden.$B$BDiese Ruinen sind kein Ort für Unvorsichtige. Die Luft ist schwer von unsichtbaren Kräften, und einige würden lieber ihre Geheimnisse bewahrt wissen.$B$BGeht vorsichtig vor, $N. Findet diese Relikte, und wir werden erfahren, welche Geschichten sie noch erzählen können.  ', 'Sammelt 8 Relikte von Eldre’Thar aus den Ruinen.', '', 'Kehrt zu Scharfseher Erdensang in Sonnenfels in den Steinkrallengebirgen zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50124 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50124, 'deDE', 'Arkanes Erbe  ', 'Die Geister der Hochgeborenen sind weiterhin an diese Ruinen gebunden - unfähig oder nicht willens, diesen Ort zu verlassen. Ihre Arroganz und Macht haben sie über Jahrhunderte an diesen Ort gefesselt, wodurch das Gleichgewicht des Landes verzerrt wurde.$B$BDas darf nicht so bleiben. Nehmt dieses Hochgeborenen-Gefängnis, ein Gefäß, durchdrungen von uralter Magie. Nutzt es, um die verweilenden Geister zu binden und sie aus ihrem Leid zu befreien.$B$BSeid gewarnt, $N. Nicht alle werden sich widerstandslos fügen. Einige werden kämpfen - verzweifelt daran festhaltend, was von ihrer Vergangenheit übrig geblieben ist.  ', 'Nutzt das Hochgeborenen-Gefängnis, um 5 Wütende Geister eines Hochgeborenen innerhalb der Ruinen von Eldre’Thar zu binden.', '', 'Kehrt zu Scharfseher Erdensang in Sonnenfels in den Steinkrallengebirgen zurück.  ', 'Wütender Geist eines Hochgeborenen gebunden', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50125 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50125, 'deDE', 'Zurück in die Tiefen!  ', 'Das Gleichgewicht dieses Landes wurde gestört. Eine Präsenz ist hier, eine, die nicht in diese Welt gehört. Ich kann sie unter der Oberfläche spüren, wie sie sich windet und versucht, durch die Risse der Realität zu dringen.$B$BEine uralte Säule steht am Eingang des Tals, errichtet vor langer Zeit von jenen, die ihre Macht verstanden. Nutzt sie, und vielleicht offenbart sich euch, was sich jenseits des Schleiers verbirgt.$N$NGeht, $N. Tretet in das verborgene Reich und vernichtet die Kreaturen, bevor sie in unsere Welt eindringen.  ', 'Tötet 10 Eindringende Zermalmer.', '', 'Kehrt zu Scharfseher Donnerhuf in Sonnenfels in den Steinkrallengebirgen zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50126 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50126, 'deDE', 'Herold Aph\'lass', 'Diese Manifestationen, sie sind nur Echos einer viel größeren Macht. Etwas spricht zu ihnen, zieht sie aus den Tiefen hervor.$B$BEin Name weht durch den Wind... Aph’lass. Es ist nicht von dieser Welt. Und es darf nicht hier bleiben.$B$BFindet es und beendet seine Verderbnis, bevor sie sich ausbreitet. Denkt daran, dass eine uralte Säule am Eingang des Tals steht - ihr müsst sie nutzen, um Aph’lass zu sehen.  ', 'Erschlagt Herold Aph\'lass.', '', 'Kehrt zu Scharfseher Donnerhuf in Sonnenfels in den Steinkrallengebirgen zurück.  ', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50127 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50127, 'deDE', 'Hüter der Flamme  ', 'Magie ist eine Kraft, die Gleichgewicht erfordert. Wenn dieses Gleichgewicht gestört wird, können die Folgen katastrophal sein.$B$BTief in den Steinkrallenbergen sickert ein seltsamer Schlamm aus den Felsen. Er pulsiert mit instabiler Energie - roh, unberechenbar und höchst besorgniserregend.$B$BIch benötige eine Probe, um seine Eigenschaften zu untersuchen. Sammelt etwas von diesem Schluchtschleim für mich. Falls er eine Bedrohung darstellt, müssen wir schnell handeln.', 'Sammelt 6 Schluchtenschlamm in den Steinkrallengebirgen.', '', 'Kehrt zu Magistera Selwyn nach Sonnfels in den Steinkrallenbergen zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50128 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50128, 'deDE', 'HÜTET EUCH VOR FELSKIEFER!  ', 'Der Krallenbecken-See ist nicht mehr sicher.$B$BEin monströser Jäger - Cragjaw - lauert in seinen Gewässern und schlägt gegen jeden zu, der es wagt, sein Revier zu betreten. Fischer, Reisende, sogar meine Späher... alle sind seinen Kiefern zum Opfer gefallen.$B$BSolltet ihr dieses Biest töten, bringt Schildwache Alenia Sternenbrise seinen massiven Zahn als Beweis. Die Menschen dieser Lande müssen wissen, dass die Bedrohung gebannt wurde.', 'Tötet Felskiefer und nehmt Felskiefers riesigen Zahn.', '', 'Kehrt zu Schildwache Alenia Sternenbrise nach dem Steinkrallengipfel in den Steinkrallenbergen zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50129 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50129, 'deDE', 'Erinnerungen einfangen  ', 'Die Hochgeborenen hielten sich einst für unsterblich, für Götter unter Sterblichen. Doch nun sind sie kaum mehr als Echos ihrer eigenen Arroganz.$B$BIhre Geister wandeln noch immer durch die Ruinen von Eldre’Thar, an eine Vergangenheit gekettet, die längst vergangen ist. Ihre Anwesenheit verzerrt den natürlichen Fluss der Magie und bedroht das Gleichgewicht dieses Landes.$B$BWir müssen verstehen, warum sie hier verweilen. Sammelt Relikte von Eldre’Thar. Vielleicht offenbaren sie uns, was diese Seelen bindet.', 'Sammelt 8 Relikte von Eldre’Thar aus den Ruinen.', '', 'Kehrt zu Magistera Selwyn nach dem Steinkrallengipfel in den Steinkrallenbergen zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50130 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50130, 'deDE', 'Arkanes Erbe  ', 'Die Hochgeborenen waren Meister der arkanen Künste, doch ihre Arroganz übertraf sogar ihre Macht. Ihr Wissen ist von unschätzbarem Wert, doch ihre ruhelosen Geister stören die natürliche Ordnung.$B$BDieses Hochgeborenen-Gefängnis wurde mit uralter Magie durchdrungen. Nutzt es, um die rastlosen Geister in den Ruinen von Eldre’Thar zu binden und sie aus ihrem Leid zu erlösen.$B$BSeid gewarnt - einige werden sich widersetzen. Verzweiflung macht selbst die Toten gefährlich.', 'Nutzt das Hochgeborenen-Gefängnis, um 5 Wütende Geister eines Hochgeborenen innerhalb der Ruinen von Eldre’Thar zu binden.', '', 'Kehrt zu Magistera Selwyn nach dem Steinkrallengipfel in den Steinkrallenbergen zurück.', 'Wütender Geist eines Hochgeborenen gebunden', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50131 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50131, 'deDE', 'Zurück in die Tiefen!  ', 'Etwas Unnatürliches lauert jenseits unseres Blicks, verborgen im Schleier zwischen den Welten. Ich kann spüren, dass es uns beobachtet.$B$BEine heilige Säule steht am Eingang des Tals. Sie wurde vor langer Zeit errichtet, um Mächte zu bannen, die nicht in diese Welt gehören. Doch die Barriere schwächt sich.$B$BBerührt die Säule und tretet durch den Schleier. Findet heraus, was in den Schatten lauert, und vernichtet es, bevor es in unsere Welt eindringen kann.', 'Tötet 10 Eindringende Zermalmer.', '', 'Kehrt zu Schildwache Alenia Sternenbrise nach dem Steinkrallengipfel in den Steinkrallenbergen zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50132 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50132, 'deDE', 'Herold Aph\'lass', 'Diese Kreaturen sind nur Fragmente - Reflexionen von etwas viel Größerem.$B$BEtwas ruft sie, zieht sie in unsere Welt. Ein Name hallt durch den Wind... Aph’lass. Es gehört nicht in diese Welt. Es darf hier nicht bleiben.$B$BFindet es und beendet seine Verderbnis, bevor sie sich weiter ausbreitet.$B$BDenkt daran: Die heilige Säule am Eingang des Tals ist der Schlüssel. Ohne sie werdet ihr Aph’lass nicht sehen können.', 'Erschlagt Herold Aph\'lass.', '', 'Kehrt zu Schildwache Alenia Sternenbrise nach dem Steinkrallengipfel in den Steinkrallenbergen zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50133 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50133, 'deDE', 'Verteidigung von Mor’shan', 'Lok\'tar, $R!$B$BDie Kampagne hat begonnen! Bald wird ganz Eschental in der Hand der Horde sein. Wie erwartet, zwingt unsere Offensive den Feind, unsere Verteidigung an jeder Position zu testen.$B$BHier in Mor\'shan wurde ihr Angriff zerschlagen! Doch je enger sich die Schlinge zieht, desto verzweifelter werden sie.$B$BErkundet die Region, $C. Findet überlebende Nachtelfen und schaltet sie aus, bevor sie sich für einen Gegenangriff sammeln können!', 'Tötet 5 Scharmützler des Eschentals und 5 Bogenschützen des Eschentals.', '', 'Kehrt zu Kadrak an der Schutzwall von Mor\'shan im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50134 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50134, 'deDE', 'Finde Gorat!', 'Gorat gehört zu den fähigsten Attentätern der Horde. Sein Name allein jagt unseren Feinden Angst ein. Er ist zuverlässig, unerschütterlich loyal und - bis jetzt - immer pünktlich gewesen.$B$BEtwas stimmt nicht, $C. Gorats Auftrag war es, den Anführer dieses Angriffs auf die Schutzwälle zu eliminieren. Er sollte sich in der Nähe des Himmelssturzbaches aufhalten.$B$BFindet ihn!', 'Findet Gorat in der Umgebung der Schutzwall von Mor’shan.', '', 'Kadrak an der Schutzwall von Mor\'shan will, dass Ihr die Region nach Gorat absucht.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50135 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50135, 'deDE', 'Letzter Bericht', '<Gorat hustet schwer, während er mit letzter Kraft spricht.>$B$BEs... war ein Hinterhalt... Wir kehrten zur Schutzwall zurück... Alle... verloren... Wir... haben ihren Anführer gefunden...$B$B<Gorat füllt mühsam eine Phiole mit seinem eigenen Blut.>$B$BMeine Krieger... müssen gerächt werden... Bringt dies... zu Kadrak... Er wird wissen... was zu tun ist...$B$B<Gorat haucht sein letztes Leben aus.>', 'Bringt die Phiole mit Gorats Blut zu Kadrak an der Schutzwall von Mor’shan.', '', 'Kehrt zu Kadrak an der Schutzwall von Mor\'shan im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50136 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50136, 'deDE', 'Sofort zu Dinah!', 'Die Horde hat vielleicht nie einen treueren Kämpfer gekannt als Gorat. Er hat noch nie eine Mission unvollendet gelassen - und es scheint, als ob er das auch jetzt nicht zulassen würde.$B$BGorat ließ Euch diese Phiole bringen - und das sicher nicht ohne Grund. Dinah ist die Tochter eines angesehenen Schamanen und mächtiger, als sie scheint. Bringt ihr die Phiole und betet, dass wir nicht zu spät sind.', 'Bringt die Phiole mit Gorats Blut zu Dinah Halbmond an der Schutzwall von Mor’shan.', '', 'Kehrt zu Dinah Halbmond an der Schutzwall von Mor\'shan im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50137 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50137, 'deDE', 'Gorats Rache', 'Das Schicksal lächelt uns heute zu, $R. Gorats Blut war noch frisch genug für ein besonderes Ritual, das ich als Kind erlernt habe.$B$BVerwendet dieses durchdrungene Blut auf Gorats Leichnam, um seinen Geist zu beschwören. Erfüllt seinen letzten Willen und kehrt dann zu Kadrak zurück.$B$BMöge der Geist Euch schützen, Freund.', 'Verwendet Gorats machterfülltes Blut auf seinem Leichnam und besiegt Hauptmann Elendilad.', '', 'Kehrt zu Kadrak an der Schutzwall von Mor\'shan im Brachland zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50138 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50138, 'deDE', 'Drecksarbeit', 'Hört gut zu, $C.$B$BIn dieser Region befehligen drei Anführer die Elfen und konzentrieren ihre Angriffe auf das Holzfällerlager. Ihr Tod wird Panik und Unordnung stiften - genau das, was wir brauchen, um unsere Mission abzuschließen.$B$BWenn Ihr schon dabei seid, nehmt auch ihre Späher mit. Je weniger Augen auf uns gerichtet sind, desto besser.', 'Tötet Beschützerin Dorinar, Beschützer Arminon, Beschützer Endolar und 12 Späher des Eschentals.', '', 'Kehrt zu Krokk im Holzfällerlager des Kriegshymnenklans in Eschental zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50139 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50139, 'deDE', 'Dämonenpflicht', 'Hört zu, $C, ich weiß, dass der Angriff auf Splitterholz alle auf Trab hält, aber wenn wir die Satyrportale ignorieren, stehen wir bald vor einer noch größeren Bedrohung.$B$B<Locke blickt Euch regungslos an.>$B$BIhr wisst nichts von den Portalen?$B$BNordöstlich von hier, in Satyrnaar und Xavian, versuchen Satyrn, Dämonen zu beschwören, um ihre Reihen zu verstärken.$B$BWir müssen diese Portale versiegeln, egal, welche Bedrohungen uns hier erwarten!', 'Schließt 12 Satyrportale in Satyrnaar und Xavian.', '', 'Kehrt zu Locke Okarr in Splitterholzposten in Eschental zurück.', 'Satyrportale geschlossen', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50140 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50140, 'deDE', 'Tweedle ist dumm', 'Verzögerungen, Verzögerungen, Verzögerungen! Ich bin am Ende meiner Geduld!$B$BMan hat mir gesagt, dieser Goblin sei ein Genie - dass er Gadgets entwickelt, die den Feind in die Knie zwingen. Bisher hat er nichts als leere Versprechungen geliefert. Wenn er mich noch einmal warten lässt, werfe ich ihn eigenhändig von der Klippe!$B$BGeht zu ihm, $C, und lasst ihn wissen, dass meine Geduld am Ende ist!', 'Sprecht mit Twidel nahe Astranaar im Eschental.', '', 'Hauptmann Goggath will, dass Ihr mit Twidel bei Höllschreis Wacht sprecht.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50141 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50141, 'deDE', 'Sie haben uns die Bombe gelegt!', 'Ich bin ganz nah dran! Ich habe genug explosive Zutaten aus unserem Arsenal geborgen, um genau die Bomben herzustellen, die Goggath will.$B$BAber ich brauche etwas, um sie zu stabilisieren... Ton. Und nicht irgendeinen Ton - mondberührten Ton.$B$BDieser Ton ist nur an den nördlichen Ufern des Astranaar-Sees zu finden. Seine Eigenschaften sind genau das, was ich brauche, um das volle Potenzial meiner Entdeckung auszuschöpfen!', 'Sammelt 10 Proben mondberührten Tons.', '', 'Kehrt zu Twidel in der Nähe von Astranaar in Eschental zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50142 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50142, 'deDE', 'Blastranaar!', 'Die Elfen von Astranaar rüsten sich für einen vernichtenden Angriff auf unser Lager.$B$BWir werden nicht warten, bis sie den ersten Schlag ausführen. Ihr werdet ihnen den Tod von oben bringen!$B$BDie Zerstörung von Astranaar wird ein Dolchstoß in das Herz der Nachtelfen sein! Sprecht mit Thraka, wenn Ihr bereit seid, den Angriff zu starten.$B$BZielt genau, $C.', 'Verwendet Tweedles improvisierte Sprengsätze, um 20 Schildwachen von Astranaar zu töten und 10 Schleudern von Astranaar zu zerstören.', '', 'Kehrt zu Hauptmann Goggath in Splitterholzposten in Eschental zurück.', 'Schildwache von Astranaar ausgelöscht', 'Schleuder von Astranaar zerstört', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50143 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50143, 'deDE', 'Mission: Unwahrscheinlich', 'Also sucht Ihr nach Arbeit? Gut. Wir brauchen jeden kampffähigen Krieger, um die Allianz in Schach zu halten.$B$BIm Holzfällerlager des Kriegshymnenklans führt Krokk das Kommando, aber ihm fehlen Leute. Die Elfen sabotieren ständig unsere Operationen, und wir dürfen ihnen keinen Vorteil gewähren.$B$BMacht Euch auf den Weg und meldet Euch bei Krokk. Er wird Euch sagen, was zu tun ist.', 'Reist zum Holzfällerlager des Kriegshymnenklans und meldet Euch bei Krokk.', '', 'Reist zum Holzfällerlager des Kriegshymnenklans und meldet Euch bei Krokk.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50144 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50144, 'deDE', 'Baumstümpfe machen', 'Wie Ihr inzwischen wisst, sollen wir einen Diplomaten aus dem belagerten Holzfällerlager eskortieren. Doch so wie die Lage steht, ist das schlicht unmöglich. Die Elfen haben tausend Pfeile auf die Ausgänge des Lagers gerichtet. Wir müssen zuerst für Ablenkung sorgen - etwas, das ihre Augen für eine Weile von unserem Ziel ablenkt.$B$BMein Plan ist einfach. Nehmt diese Axt und fällt die Bäume, die sie hier gepflegt haben.', 'Verwendet die Splitterholzaxt, um Eichen des Eschentals im Holzfällerlager des Kriegshymnenklans zu fällen.', '', 'Kehrt zu Krokk im Holzfällerlager des Kriegshymnenklans in Eschental zurück.', 'Eiche des Eschentals gefällt', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50145 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50145, 'deDE', 'Astranaar in Flammen!', '$C, Astranaar steht in Flammen!$B$BSchnell, nehmt diesen Wassereimer und löscht die Brände, bevor nichts mehr übrig bleibt!', 'Löscht 15 Brände in Astranaar.', '', 'Kehrt zu Schildwache Thenysil in Astranaar in Eschental zurück.', 'Feuer gelöscht', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50146 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50146, 'deDE', 'Feuer erwidern', 'Die Brände sind größtenteils eingedämmt, aber wir müssen etwas gegen diese Windreiter unternehmen!$B$BSchnell, $N, springt in eine unserer Schleudern und erwidert das Feuer!', 'Verwendet eine Schleuder von Astranaar, um 15 Eindringlinge der Horde abzuschießen.', '', 'Kehrt zu Schildwache Thenysil in Astranaar in Eschental zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50147 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50147, 'deDE', 'Was da auferstanden ist', 'Als ich diese... Dinge... aus der Tiefe aufsteigen spürte, kam ich, um sie zurückzudrängen. Ich fand den Leichnam Eures Freundes und zog ihn so weit wie möglich fort, bevor eines meiner Beine versagte. Jetzt kann ich nur noch versuchen, diese verderbten Ranken aufzuhalten.$B$BEs gibt ein Wesen, das die Ranken und Augen kontrolliert. Es nennt sich Bote Aphotic - ich bin sicher, Ihr habt seine Schreie aus dem Süden gehört.$B$BDer Bote bleibt außerhalb meiner Reichweite, aber Ihr könntet ihn erreichen. Falls Ihr es wagt...', 'Tötet Bote Aphotic und 5 Ranken aus der Unterwelt.', '', 'Kehrt zu Bewahrer Herzensweg am Schrein von Aessina in Eschental zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50148 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50148, 'deDE', 'Verdorbener Einfluss?', 'Während Ihr die Habseligkeiten des toten Furbolgs durchsucht, fällt Euch ein kleiner Talisman auf den Boden - ganz offensichtlich von Trollen gefertigt.$B$BAls Ihr ihn berührt, spürt Ihr sofort, dass etwas nicht stimmt. Eine Aura von Unheil und Verderbnis durchdringt das Holz.$B$BIhr wickelt ihn vorsichtig in ein Stück Stoff und nehmt ihn mit. Vielleicht kann Raene Wolfsläufer in Astranaar mehr darüber herausfinden.', 'Bringt das Trolltotem zu Raene Wolfsläufer in Astranaar, Eschental.', '', 'Bringt den Trolltalisman zu Raene Wolfsläufer in Astranaar in Eschental.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50149 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50149, 'deDE', 'Weisheit der Worgen', 'Der Mann dort drüben ist als Hephaestus Pilgrim bekannt. Er ist ein Worgen.$B$BEr und seine Schwester sind vor Kurzem nach Astranaar gekommen, auf der Suche nach einem Ort zum Leben. Soweit ich herausfinden konnte, will er den Irdenen Ring bei den Nachwirkungen des Kataklysmus unterstützen. Seine Schwester ist noch mächtiger. Sie ist nach Osten aufgebrochen, um den Schamanen beim Kampf gegen den Vulkan zu helfen.$B$BVielleicht kann er Euch helfen. Bringt ihm den Talisman und seht, was er darüber weiß.', 'Bringt das Trolltotem zu Ravena in Astranaar, Eschental.', '', 'Bringt den Trolltalisman zu Ravena in Astranaar in Eschental.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50150 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50150, 'deDE', 'Kein ganz so bezauberndes Leben', 'Die Furbolgs reißen sich stets Dinge unter den Nagel, die ihnen nicht gehören.$B$B<Ravena dreht den Talisman in der Hand, bevor sie ihn verächtlich wegwirft.>$B$BDas Schnitzwerk ist mit einer Magie durchsetzt, die jene langsam korrumpiert, die keine Trolle sind. Ich habe gehört, dass die Furbolgs so etwas schon früher getan haben. Manche lernen es nie.$B$BIch bezweifle nicht, dass sie die meisten Talismane in ihrer Zuflucht, dem Distelfellhort, horten.', 'Sammelt 8 Trolltotems.', '', 'Kehrt zu Ravena in Astranaar in Eschental zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50151 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50151, 'deDE', 'Die reinigende Strömung', 'Die Flammen wüten unkontrolliert, verschlingen das Land und lassen nichts als verbrannte Erde zurück. Doch dies ist kein gewöhnliches Feuer. Etwas weitaus Finstereres speist die Flammen.$B$BIch bin nicht allein in diesem Kampf. Arctanus, einer der Irdenen Ring, ist bereits zur Quelle dieser Verwüstung aufgebrochen. Er sucht den Beistand der Elemente, um das Feuer einzudämmen.$B$BGeht zu ihm, $N, und bietet ihm Eure Hilfe an. Wenn wir nicht schnell handeln, wird Eschental zu Asche vergehen. Arctanus ist irgendwo am Strand im Westen von Eschental.', 'Findet Arctanus am Strand im Westen von Eschental.', '', 'Findet Arctanus nahe des Strandes im Westen von Eschental.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50152 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50152, 'deDE', 'Inferno', 'Die Flammen im westlichen Teil von Eschental, nahe des Strandes, sind außer Kontrolle geraten. Was als fahrlässiges schamanisches Ritual begann, hat etwas weit Schlimmeres hervorgebracht - ein lebendiges Inferno.$B$BEin riesiger Feuerelementar wandelt nun über das Schlachtfeld, gestärkt von jeder Glut. Dies ist kein einfacher Geist der Flamme, sondern eine entfesselte Kraft der Zerstörung, genährt von Krieg und Chaos.$B$BWenn wir ihn nicht aufhalten, wird er Eschental in eine rauchende Ödnis verwandeln.', 'Besiegt Lord Magmathar.', '', 'Kehrt zu Arctanus nahe des Strandes im Westen von Eschental zurück.', 'Lord Magmathar besiegt', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50153 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50153, 'deDE', 'Die Flammen ersticken', 'Das große Feuer wütet nicht allein. Geringere Feuerelementare streifen über das Schlachtfeld, nähren sich an der Verwüstung und wachsen weiter an Macht.$B$BWenn sie nicht gestoppt werden, werden sie sich weiter vereinen und zu etwas noch Gefährlicherem heranwachsen.$B$BSucht diese umherstreifenden Flammen und löscht sie, bevor sie sich ausbreiten können.', 'Tötet 6 Lavawüter.', '', 'Kehrt zu Arctanus nahe des Strandes im Westen von Eschental zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50154 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50154, 'deDE', 'Die berüchtigten Grells', 'Die Grellkin waren stets eine Plage, doch nun hat sich etwas verändert. Sie verhalten sich nicht mehr wie wild umherirrende Kreaturen - sie kämpfen mit Zielstrebigkeit, als ob eine dunkle Macht sie lenken würde.$B$BUnsere Späher haben ihre Bewegungen bis zu einer Höhle tief in Splitterholzposten zurückverfolgt. Die Quelle ihrer neuen Aggression ist kein wildes Tier, sondern ein Dämon - ein Teufelswache der Legion, der ihre Gedanken mit Versprechungen von Macht verdorben hat.$B$BWir dürfen diese Verderbnis nicht weiter wuchern lassen. Dringt in die Höhle ein, rottet die Grellkin aus und vernichtet ihren teuflischen Meister, bevor sein Einfluss sich weiter ausbreitet.', 'Tötet 8 Dornpestgrell und Vornithar in der Höhle bei Splitterholzposten.', '', 'Kehrt zu Loruk Waldläufer in Splitterholzposten in Eschental zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50155 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50155, 'deDE', 'Die Kluft versiegeln', 'Ihr habt es gespürt, nicht wahr? Den Gestank von Teufelsmagie, die unnatürliche Hitze in der Luft...$B$BEine Kluft brennt noch immer im Herzen dieser Höhle - ein Riss in der Realität, der diese Welt mit dem Wirbelnden Nether verbindet. Dort nahm die Verderbnis ihren Anfang. Wenn wir nicht schnell handeln, werden noch mehr Dämonen hindurchtreten - stärkere als zuvor.$B$BVerschließt das Portal, bevor noch mehr Unheil über diese Welt hereinbricht.', 'Versiegelt das Dämonenportal in der Höhle bei Splitterholzposten.', '', 'Kehrt zu Loruk Waldläufer in Splitterholzposten in Eschental zurück.', 'Dämonenportal geschlossen', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50156 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50156, 'deDE', 'Die goblin’sche Denkriege', 'Das Holzfällerlager des Kriegshymnenklans stinkt nach Zerstörung. Sie fällen nicht nur Bäume - sie schlachten das Land, bis nichts als Fäulnis übrig bleibt.$B$BIhr Anführer, ein Goblin-Ingenieur namens Leitender Bombentechniker Splodier, ist für ihre schlimmsten Kriegsmaschinen verantwortlich. Seine Geräte haben unsere Wälder in Brand gesetzt und uralte Haine in Rauch und Asche verwandelt.$B$BDieses Monster darf keine weitere Bombe mehr bauen. Findet ihn, nehmt seinen Kopf und bringt ihn zu mir. Die Horde soll sehen, was mit jenen geschieht, die unsere Lande niederbrennen!', 'Holt Euch Splodiers Kopf.', '', 'Kehrt zu Schildwache Luciel Sternwisper in Waldeslied in Eschental zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50157 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50157, 'deDE', 'Flüstern aus der Tiefe', 'Spürt Ihr es nicht auch? Die Luft ist schwer, die Erde grollt unter uns. Etwas Uraltes rührt sich in den Tiefen dieses Landes.$B$BIch habe die Stimmen in meinen Träumen gehört - ein Flüstern von unten, in einer Sprache, die ich nicht kenne, aber dennoch verstehe. Die Geister schreien uns eine Warnung zu, doch ihr Ruf wird von etwas... Unnatürlichem übertönt.$B$BIch muss mit ihnen in Kontakt treten. Sucht in der Nähe des Strandes im Westen von Eschental nach Antworten.', 'Findet Arctanus am Strand im Westen von Eschental.', '', 'Findet Arctanus nahe des Strandes im Westen von Eschental.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50158 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50158, 'deDE', 'Dämonenpflicht', 'Hört zu, $C, ich weiß, dass der Krieg gegen die Horde alle beschäftigt, aber wenn wir die Satyrportale ignorieren, werden wir nach diesem Konflikt einer noch größeren Bedrohung gegenüberstehen.$B$B<Schildwache Luciel Sternwisper mustert Euch mit ernster Miene.>$B$BIhr wisst nichts von den Portalen?$B$BIm Nordosten, in den Regionen von Satyrnaar und Xavian, versuchen die Satyrn, Dämonen zu beschwören, um ihre Reihen zu verstärken.$B$BWährend wir gegen die Horde kämpfen, dürfen wir nicht zulassen, dass diese dunklen Mächte erstarken. Die Portale müssen geschlossen werden, bevor es zu spät ist!', 'Schließt 12 Satyrportale in Satyrnaar und Xavian.', '', 'Kehrt zu Schildwache Luciel Sternwisper in Waldeslied in Eschental zurück.', 'Satyrportale geschlossen', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50159 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50159, 'deDE', 'Die Not des Großknechts', 'Ein Unglück hat unsere Brücke getroffen, $C. Ein Erdrutsch aus den Hügeln hat Großknecht Hannen unter einem gewaltigen Felsbrocken begraben, und die Ettins, die im Hochland umherstreifen, machen alles nur noch schlimmer.$B$BWir haben alles versucht, um den Stein zu bewegen, aber er rührt sich nicht! Die Ettins schwingen riesige Keulen - primitiv gefertigt, aber unglaublich kraftvoll. Wenn wir eine in die Hände bekommen könnten, hätten wir vielleicht genug Hebelwirkung, um Hannen zu befreien.$B$BDas Problem? Ettins geben ihre Waffen nicht freiwillig her. Wenn Ihr eine wollt, müsst Ihr sie Euch nehmen.', 'Findet einen Ettin-Kriegsknüppel in den Rotkammklüften.', '', 'Kehrt zu Magistrat Solomon in Seenhain zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50160 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50160, 'deDE', 'Hebelkraft gegen Stein', 'Diese Keule ist schwerer, als sie aussieht, aber wenn wir sie richtig einsetzen, könnten wir sie unter den Felsbrocken klemmen und ihn weit genug anheben, um Großknecht Hannen zu befreien.$B$BBegebt Euch zur Brücke und benutzt den Ettin-Kriegsknüppel, um ihn zu retten!', 'Benutzt den Ettin-Kriegsknüppel am riesigen Felsbrocken, um Großknecht Hannen zu befreien.', '', 'Kehrt zu Magistrat Solomon in Seenhain zurück.', 'Großknecht Hannen befreit', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50161 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50161, 'deDE', 'Rache', '$C. Diese Ettins haben diese Region viel zu lange terrorisiert, und nach dem, was sie mir angetan haben, werde ich keine Ruhe finden, bis sie dafür bezahlen!$B$BWenn Ihr bereit seid, dann sorgen wir dafür, dass sie sich zweimal überlegen, ob sie Seenhain erneut angreifen. Es wird Zeit, dass sie lernen, wem dieses Land wirklich gehört!', 'Besiegt 5 Ettins in den Rotkammhöhen.', '', 'Kehrt zu Magistrat Solomon in Seenhain zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50162 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50162, 'deDE', 'Noch bei der Bedrohungsanalyse', 'Diese verfluchten, wertlosen Gnolle führen nichts Gutes im Schilde. Ich kann es in meinen Knochen spüren. Die Aktivität der Gnolle hat sich in den letzten zwei Wochen verdreifacht. Ich fürchte, sie könnten sich auf einen Angriff auf Seenhain vorbereiten.$B$BIhr müsst zu den Gnollcamps in dieser Gegend gehen und alle Informationen sammeln, die Ihr finden könnt. Überprüft die Lager direkt nördlich, östlich und südöstlich von hier. Bringt alles zurück, was Ihr findet.', 'Stehlt die Schlachtpläne der Gnolle, die Befehle der Gnolle und den Strategieführer der Gnolle.', '', 'Kehrt zu Wachhauptmann Parker im Rotkammgebirge zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50163 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50163, 'deDE', 'Parkers Bericht', '<Wachhauptmann Parker reicht Euch einen Bericht.>$B$BHier ist er, $N. Bringt meinen Bericht nach Seenhain, nördlich dieses Turms, und übergebt ihn an Magistrat Solomon. Sagt ihm, dass wir eine umfassendere Lösung für das Gnollproblem brauchen. Schlagt Sprengstoff vor. Jede Menge Sprengstoff.$B$BUnd bleibt auf der Straße, wenn Euch Euer Leben lieb ist.$B$BWas Euch lieb sein sollte, ist Euer Leben, falls Ihr nicht wisst, was gut für Euch ist. Jetzt wisst Ihr es... Ach, vergesst es einfach!', 'Überbringt Parkers Bericht an Magistrat Solomon in Seenhain im Rotkammgebirge.', '', 'Überbringt Parkers Bericht an Magistrat Solomon in Seenhain im Rotkammgebirge.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50164 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50164, 'deDE', 'Wir müssen uns vorbereiten!', 'Jeden Tag verschwinden mehr unserer Bürger. Wir glauben, dass die Gnolle dahinterstecken, aber wir haben keine Ahnung, wohin sie die Leute bringen! Wenn Ihr uns helfen wollt, die Gnolle zu besiegen und unsere Leute zu finden, müsst Ihr richtig ausgerüstet sein. Leider sind wir völlig ohne Gnomcorder. Ohne einen Gnomcorder gibt es keine Möglichkeit, mit Euch zu kommunizieren, während Ihr draußen seid.$B$BUnser letzter funktionierender Gnomcorder wurde letzte Woche von marodierenden Murlocs gestohlen. Ihr müsst zum Immerruhsee östlich von hier gehen und ihn zurückholen!', 'Bergt den Gnomcorder.', '', 'Kehrt zu Magistrat Solomon in Seenhain zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50165 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50165, 'deDE', 'Den Gnomcorder abstimmen', 'Der Gnomcorder scheint noch zu funktionieren. Ich habe Euch verbunden, jetzt müssen wir ihn nur noch auf Empfang abstimmen.$B$BBegebt Euch nördlich aus Seenhain heraus und folgt der Straße bis zum Friedhof. Sobald Ihr den Friedhof erreicht, werde ich versuchen, ein Signal zu senden. Ihr müsst ihn exakt an der richtigen Stelle benutzen, also probiert es an mehreren Positionen aus, bis Ihr den richtigen Punkt findet.', 'Begebt euch zum Friedhof von Seenhain und stimmt den Gnomcorder ab.', '', 'Kehrt zu Magistrat Solomon in Seenhain zurück.', 'Gnomcorder justiert', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50166 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50166, 'deDE', 'Schluchtengang', 'Als Erstes müsst Ihr durch die Rotkammklüfte marschieren, direkt nördlich von Seenhain. Tötet jeden Gnom, den Ihr seht... Oh, habe ich Gnom gesagt? Ich meinte Gnoll. Tötet jeden GNOLL, den Ihr seht, und bringt ihre Halsbänder als Beweis mit.$B$BIch muss Euch warnen, $N; während Ihr dort draußen seid, haltet Ausschau nach Ettins. Meine Späher haben berichtet, dass riesige, blutrünstige Ettins in den Klüften umherziehen. Bleibt ihnen besser aus dem Weg, wenn Ihr am Leben bleiben wollt.', 'Sammelt 10 Halsbänder der Rotkammgnolle.', '', 'Kehrt zu Magistrat Solomon in Seenhain zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50167 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50167, 'deDE', 'Knurrzahn muss sterben!', 'Als Nächstes müsst Ihr Knurrzahn finden, $N. Er hält sich irgendwo im nördlichen Teil der Rotkammklüfte auf. Tötet diesen Bastard und kehrt zu mir zurück, wenn Ihr Erfolg hattet.', 'Tötet Knurrzahn.', '', 'Kehrt zu Magistrat Solomon in Seenhain zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50168 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50168, 'deDE', 'Wie ein Furz im Wind', 'Wir wurden letzte Nacht überfallen oder war es die Nacht davor? Ich weiß es nicht mehr genau. Seit drei Tagen kriege ich kein Auge zu. Wahrscheinlich verliere ich schon den Verstand.Gnolle haben unsere Vorratslager geplündert. Alles haben sie mitgenommen! Rein und raus, lautlos wie ein Furz im Wind! Meine Soldaten sahen sie zu den Schluchten nordwestlich von hier laufen. Bringt unsere Vorräte zurück, $C!', 'Bergt 8 Vorratskisten des Rotkammgebirges.', '', 'Kehrt zu Marschall Marris in Seenhain zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50169 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50169, 'deDE', 'Die Dämmerreißer-Bedrohung', 'Die Dunkelheit zieht unaufhaltsam nach Dämmerwald. Meister Carevins Aufgabe ist es, das Böse zu vertreiben. Durch unsere Bemühungen bleibt Dunkelhain sicher.$B$BDu hältst dich für würdig, dich uns anzuschließen?$B$BAuch ich dachte einst so. Enttäuscht von der Untätigkeit der Nachtwache, schloss ich mich Meister Carevin an. Wenn du dich beweisen willst, dann nicht mit Worten.$B$BZeige dein Können gegen die Worgen der Dämmerreißer im Hellenwaldhain. Dann werden wir sehen, ob du das Zeug dazu hast, diese Monster zu jagen.', 'Tötet 10 Worgen der Dämmerreißer in Dämmerwald.', '', 'Kehrt zu Calor in Dunkelhain im Dämmerwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50170 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50170, 'deDE', 'Die heulenden Schatten', 'Die Worgen-Plage hat sich über den Hellenwald hinaus ausgebreitet. Im Norden, entlang der Flussufer von Dämmerwald, lauern diese verfluchten Bestien im Schatten und warten nur darauf, zuzuschlagen.$B$BDiese Worgen sind gerissener, $N, aber ebenso blutrünstig. Sie weben dunkle Magie und beschwören Schatten, die ihre Beute aus der Ferne zerreißen.$B$BBegebt Euch zum Fluss im Norden von Dämmerwald und vernichtet die Schattenwirker, bevor ihre Verderbnis sich weiter ausbreitet!', 'Tötet 7 Schattenwirker der Dämmerreißer in Dämmerwald.', '', 'Kehrt zu Calor in Dunkelhain im Dämmerwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50171 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50171, 'deDE', 'Zu Jägerin Selara!', 'Die Worgen werden jede Nacht dreister und wagen sich immer näher an Dunkelhain heran. Wir kämpfen seit Jahren gegen sie, aber jetzt… jetzt ist es anders. Sie verhalten sich nicht mehr wie wilde Tiere. Sie haben ein Ziel. Sie jagen mit Taktik.$B$BIch habe eine neue Verbündete - Jägerin Selara. Sie kämpfte einst gegen das Rudel der Dämmerreißer, doch jetzt versteckt sie sich in Rabenflucht.$B$BWenn jemand weiß, was diese Worgen vorhaben, dann sie. Finde Selara und bring in Erfahrung, was sie weiß. Dunkelhains Überleben könnte davon abhängen.', 'Findet Jägerin Selara in Rabenflucht.', '', 'Findet Jägerin Selara in Rabenflucht.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50172 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50172, 'deDE', 'Würger und Vollstrecker', 'Wir werden uns den gefährlichsten Worgen stellen, die diese Wälder befallen haben.$B$BUnter den Dämmerreißern haben sich die Würger und Vollstrecker als die tödlichsten erwiesen. Sie haben sich in der Mine im Nordosten niedergelassen.$B$BSogar aus der Ferne kannst du das Licht ihrer Lagerfeuer erkennen...', 'Tötet 6 Würger der Dämmerreißer und 6 Vollstrecker der Dämmerreißer im Mondschleier-Steinbruch.', '', 'Kehrt zu Jägerin Selara in Rabenflucht im Dämmerwald zurück.', '', '', '', '', 16000);
