INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50173, 'deDE', 'Die Bedrohung der Dämmerreißer', 'Hier, $N. Meister Carevin muss das sofort sehen.$B$B<Selara reißt vorsichtig ein Stück Pergament aus einem abgenutzten Tagebuch und reicht es dir.>$B$BWenn sich mehr wie du gegen die Dunkelheit stellen würden, müsste die Nachtwache diesen Kampf nicht allein führen. Vielleicht könnten wir dann endlich vollenden, was wir begonnen haben.', 'Bringt Selaras Notiz zu Jonathan Treuwein.', '', 'Bringt Selaras Notiz zu Jonathan Treuwein.', '', '', '', '', 16000);
