INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50174, 'deDE', 'Das Ende des Schattenfangs', 'Die Worgen im Mondschleier-Steinbruch sind nicht nur wegen ihres Fluchs dem Wahnsinn verfallen – etwas weit Schlimmeres lauert in den Tiefen der Mine.$B$BIhr Anführer, Garrik Schattenfang, hat sich mit einem Rudel im Steinbruch verschanzt. Sie kämpfen verzweifelt gegen die Kreaturen, die die Tunnel erobert haben. Er wird nicht fliehen. Er wird sich nicht ergeben. Er wird kämpfen, bis es nichts mehr zu bekämpfen gibt.$B$BUnd genau da kommst du ins Spiel, $N.$B$BGeh in den Steinbruch. Beende Garriks Blutrausch und vielleicht können wir diesen verdammten Ort zurückerobern.', 'Erschlagt Garrik Schattenfang im Mondschleier-Steinbruch.', '', 'Kehrt zu Jägerin Selara bei Rabenflucht im Dämmerwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50175 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50175, 'deDE', 'Gift in den Adern', 'Nicht nur die Worgen haben sich im Mondschleier-Steinbruch eingenistet – die Höhle ist von giftigen Monstern verseucht.$B$BDas Dämmergift-Geschmeiß gedeiht in der Dunkelheit und macht Jagd auf die Worgen und alles andere, das sich hineinwagt. Ihre Reißzähne tropfen vor Gift, und ihre Netze ersticken die Tunnel wie eine Seuche.$B$BWollen wir den Steinbruch zurückerobern, müssen diese Kreaturen vernichtet werden.$B$BGeh, $N. Verbrenne ihre Netze, zertrete ihre Brut und tilge sie aus.', 'Erschlagt 4 Netzweber der Dämmergifte, 4 Kriecher der Dämmergifte und 4 Stachelschrecks der Dämmergifte.', '', 'Kehrt zu Jägerin Selara bei Rabenflucht im Dämmerwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50176 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50176, 'deDE', 'Die Königin im Netz', 'Jeder Albtraum hat eine Quelle, und der Albtraum im Mondschleier-Steinbruch hat einen Namen: Königin Velthara.$B$BDie Worgen sprechen ihren Namen nur in Flüstern aus. Sie nennen sie die "Mutter des Giftes". Sie fürchten sie mehr als ihresgleichen.$B$BVelthara ist kein hirnloses Monster. Sie beobachtet. Sie plant. Wenn wir jetzt nicht handeln, wird sie ihre Brut über den gesamten Wald ausbreiten – die Straßen, die Siedlungen… sogar Dunkelhain.$B$BGeh in die Tiefen, $N. Finde ihr Netz… und brenne es nieder.', 'Erschlagt Königin Velthara im Mondschleier-Steinbruch.', '', 'Kehrt zu Jägerin Selara bei Rabenflucht im Dämmerwald zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50177 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50177, 'deDE', 'Die Yorgen-Worgen', 'Meine Gefährten in Rabenflucht versuchen, den wilden Worgen in diesen Wäldern zu helfen. Der Fluch kann nicht aufgehoben werden, doch der Verstand mancher kann zurückgebracht werden.$B$BDie meisten hier sind verloren, aber wir achten auf jene, die noch Erinnerungen an ihr früheres Leben bewahren.$B$BIch habe einen einsamen Worgen auf dem Yorgen-Hof gesehen. Er scharrte immer wieder an einem Haufen loser Erde zwischen den Gebäuden... Vielleicht hat er dort etwas hinterlassen?', 'Untersucht die Haufen lockerer Erde auf Yorgens Hof.', '', 'Kehrt zu Lehrling Fess im Dämmerwald zurück.', 'Ein Haufen lockerer Erde untersucht', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50178 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50178, 'deDE', 'Lieferung an Meister Harris', 'Meister Harris muss das sofort erfahren.$B$BHier, nimm dieses Bündel Leichengras mit nach Rabenflucht. Berichte ihm, was wir am Yorgen-Hof gesehen haben.', 'Bringt das Bündel Leichengras zu Oliver Harris in Rabenflucht.', '', 'Bringt das Bündel Leichengras zu Oliver Harris in Rabenflucht.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50179 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50179, 'deDE', 'Durch verfluchte Augen', 'Ich habe Jahre damit verbracht, den Fluch zu erforschen, der dieses Land heimsucht, $N. Die Worgen, die die Schatten von Dämmerwald durchstreifen, sind keine gewöhnlichen Bestien – sie sind an dunkle Magie gebunden und bleiben den meisten Augen verborgen.$B$BAber ich glaube, ich habe einen Weg gefunden, sie zu sehen.$B$BDiese Phiole enthält ein experimentelles Gebräu – Harris’ Ampulle. Trink sie, und du wirst vielleicht einen Blick auf das erhaschen, was direkt vor dir lauert.$B$BGeh in die Wälder nördlich von Dunkelhain, nahe dem Mondschleier-Steinbruch. Wenn meine Formel funktioniert, solltest du die lauernden Worgen erkennen können. Töte einen und kehre zu mir zurück… falls du überlebst.', 'Tötet einen lauernden Worgen.', '', 'Kehrt zu Oliver Harris bei Rabenflucht zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50180 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50180, 'deDE', 'Die Jagd auf die Verborgenen', 'Also funktioniert die Ampulle… und nun haben wir eine Waffe gegen diese verfluchten Bestien.$B$BAber ein Worgen ist erst der Anfang, $N. Sie schleichen entlang der Straßen, verstecken sich in den Bäumen und warten nur auf den richtigen Moment, um zuzuschlagen.$B$BDiesen Moment werden wir ihnen nicht gewähren.$B$BNimm eine weitere Dosis von Harris’ Ampulle und kehre in die Wälder nahe dem Mondschleier-Steinbruch zurück. Doch diesmal testest du die Formel nicht – du gehst auf die Jagd.$B$BTöte so viele dieser Worgen, wie du kannst. Zeig ihnen, dass wir keine Beute mehr sind!', 'Tötet 8 lauernde Worgen.', '', 'Kehrt zu Oliver Harris bei Rabenflucht zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50181 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50181, 'deDE', 'Rettung aus dem Ungewissen', 'Du bist nicht der Einzige, der sich in dieses verfluchte Gebiet wagt, $N.$B$BAria Nachtklaue, eine Späherin aus Dunkelhain, kam hierher, um nach Hinweisen zu den lauernden Worgen zu suchen. Sie hat sie gefunden… doch nun ist sie gefangen.$B$BSie versteckt sich irgendwo in Rabenflucht, unfähig zu entkommen, ohne in Stücke gerissen zu werden. Wenn du stark genug bist, kämpfe dich zu ihr durch. Führe sie hinaus, bevor die Schatten eine weitere Seele verschlingen.$B$BSei jedoch gewarnt – diese Worgen jagen nicht allein.', 'Eskortiert Aria Nachtklaue sicher aus Rabenflucht.', 'Eskortiere Aria Nachtklaue aus Rabenflucht', 'Kehrt zu Calor in Dunkelhain im Dämmerwald zurück.', '', '', '', '', 16000);
