INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50003, 'deDE', 'Zerak muss sterben', 'Nun ist die Zeit gekommen, dem Anführer des Zwielichtkults hier ein Ende zu setzen. Zerak ist der Drahtzieher hinter diesen dunklen Ritualen und der Bedrohung, die sie darstellen. Ohne ihn wird der Kult im Chaos versinken. Finde ihn am Glutnebelgipfel und töte ihn, bevor er noch mehr Macht ansammelt.', 'Finde und töte Zerak am Glutnebelgipfel.', '', 'Kehrt zu Dalra Faulschleier zurück.', '', '', '', '', 16000);
