INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50004, 'deDE', 'Botschaft nach Orgrimmar', 'Die Gefahr am Glutnebelgipfel mag vorerst gebannt sein, doch die Zwielichtkultisten werden nicht untätig bleiben. Es ist wichtig, dass die Führung in Orgrimmar über die Geschehnisse informiert wird. Nimm diesen Brief und überbringe ihn in Orgrimmar. Mit diesem Bericht werden sie den Kult besser im Auge behalten können... und hoffentlich vorbereitet sein, sollte das Zwielicht erneut zuschlagen.', 'Reise nach Orgrimmar und überbringe den Brief mit dem Statusbericht an Kar\'gosh Flammenklinge.', '', '', '', '', '', '', 16000);
