INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50007, 'deDE', 'Holzbedarf', 'Unsere Vorräte an Bauholz gehen zur Neige, und wir brauchen dringend mehr Material. Der Dämonenhügel im Eschental ist voller uralter Bäume, die ideales Holz liefern könnten. Die Gegend ist jedoch gefährlich, und das Holz muss unter widrigen Umständen gesammelt werden. Sei vorsichtig und bringe uns genug Holz zurück, um die Verteidigungsanlagen zu verstärken.', 'Sammle 10 Holzstapel aus Vul\'Gol im Dämmerwald.', '', 'Kehrt zu Mastok Wutfauch zurück.', '', '', '', '', 16000);
