INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50011, 'deDE', 'Rückkehr nach Orgrimmar', 'Deine Arbeit im Eschental war äußerst wertvoll, aber nun ist es Zeit, nach Orgrimmar zurückzukehren und dem Kommando Bericht zu erstatten. Der Zwielichtkult wird weiterhin eine Bedrohung darstellen, und wir müssen vorbereitet sein. Bringe alle gesammelten Informationen nach Orgrimmar – unsere Anführer müssen über die Lage informiert werden.', 'Reise nach Orgrimmar und mache Meldung bei Kar\'gosh Flammenklinge.', '', 'Reise nach Orgrimmar und mache Meldung bei Kar\'gosh Flammenklinge.', '', '', '', '', 16000);
