INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50006, 'deDE', 'Kristall des Zwielichts', 'Der Zwielichtkult setzt immer gefährlichere Mittel ein, um seine dunklen Pläne zu verfolgen. Es gibt Berichte über mächtige Zwielicht-Drakoniden, die einen magische Kristalle bewachen. Einer dieser Kristalle enthält vermutlich wertvolle Informationen über die nächsten Schritte des Kults. Deine Aufgabe ist es, einen dieser Drakoniden zu finden und einen Kristall zu beschaffen. Doch sei gewarnt – diese Drakoniden sind nicht nur stark, sondern von der Dunkelheit selbst durchdrungen. Der Kristall könnte uns die nötigen Einblicke geben, um dem Zwielichtkult einen Schritt voraus zu sein.', 'Besiege einen Zwielicht-Drakoniden und sammle einen magischen Kristall des Zwielichts.', '', 'Kehrt zu Loruk Waldschleicher zurück.', '', '', '', '', 16000);
