INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50008, 'deDE', 'Setzt dem ein Ende!', 'Der Dämonenhügel im Eschental wird von Zwielichtogern heimgesucht, die Chaos und Verwüstung hinterlassen. Diese Oger sind brutale Anhänger des Zwielichtkults und bedrohen die umliegenden Gebiete. Sie haben am Dämonenhügel eine Festung errichtet und greifen jeden an, der sich ihnen nähert. Es ist Zeit, ihre Zahl zu verringern und ihnen zu zeigen, dass ihr Einfluss hier nicht erwünscht ist.', 'Tötet Zwielichtoger am Dämonenhügel.', '', 'Kehrt zu Valusha zurück.', '', '', '', '', 16000);
