INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50009, 'deDE', 'Die Drachkin-Bedrohung', 'Der Dämonenstieg im Eschental ist von Drachkin besiedelt, die zu den Vasallen des Zwielichtkults gehören. Diese Kreaturen sind stark und gefährlich und greifen jeden an, der versucht, den Pfad zu passieren. Um den Weg für zukünftige Expeditionen sicher zu machen, müssen die Drachkin ausgedünnt werden.', 'Tötet Drachkin am Dämonenstieg im Eschental.', '', 'Kehrt zu Loruk Waldschleicher zurück.', '', '', '', '', 16000);
