INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50010, 'deDE', 'Forschungsfoliant für einen Goblin', 'Ein Goblin-Forscher benötigt dringend ein altes Buch – den Forschungsfolianten der Dunklen Magie. Dieser Foliant soll sich in einer Höhle am Dämonenstieg befinden, tief im Eschental. Es heißt, das Buch sei voller wertvoller Informationen über die Magie der Zwielichtkultisten. Suche die Höhle und bringe den Folianten zurück, aber sei vorsichtig – die Höhle ist voller Gefahren.', 'Finde und bringe den Forschungsfolianten der Dunklen Magie zurück.', '', 'Bringt das Buch zu Pixel.', '', '', '', '', 16000);
