INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50028, 'deDE', 'Zerstörung der Zwielichteier', 'Im Flammenschlund hat der Zwielichtkult einen weiteren abscheulichen Plan in die Tat umgesetzt: Sie haben Zwielichteier gelegt, aus denen ihre verderbten Kreaturen schlüpfen sollen. Diese Eier sind von dunkler Magie durchzogen und dürfen auf keinen Fall schlüpfen.', 'Zerstöre 20 Zwielichteier im Flammenschlund.', '', 'Kehre zu Isolde Glutschleier zurück.', 'Zwielichteier zerstört', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50029 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50029, 'deDE', 'Die Schlacht gegen Vorthalax', 'Kommandant Vorthalax, der Anführer der Zwielichtkultisten im Flammenschlund, ist die treibende Kraft hinter dieser Besetzung. Er ist mächtig und von der dunklen Magie durchdrungen, die ihm vom Kult verliehen wurde. Solange er lebt, wird der Kult seine Operationen fortsetzen. Dringe tief in den Flammenschlund vor und vernichte Kommandant Vorthalax. Dies wird das Rückgrat der Zwielichtkultisten brechen und ihre Bedrohung in Orgrimmar endgültig beenden.', 'Besiege Kommandant Vorthalax im Flammenschlund.', '', 'Kehre zu Isolde Glutschleier zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50030 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50030, 'deDE', 'Große Haken und Lächeln sollten Misstrauen hervorrufen', 'Okay, pass auf! Wir schnappen uns ein bisschen Gift von den Skorpionen, die da draußen rumkrabbeln. Aber hey, keine Panik – ich hab alles unter Kontrolle! Ich hab da was gebastelt – ein großes Totem-Dingens. Steck das einfach in den Boden, wenn der Skorpion zustechen will. Das saugt das ganze fiese Gift weg, und du gehst da raus, als wär nichts passiert. Ziemlich genial, oder?', 'Erhaltet 8 Proben Skorpidtoxin, indem Ihr das Totem der Giftextraktion in der Nähe eines giftigen Skorpids aufstellt.', '', 'Kehrt zu Fizzick zwischen Sen\'jin und dem Tal der Prüfungen in Durotar zurück.', 'Probe Skorpidtoxin', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50031 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50031, 'deDE', 'Es ist Hammerzeit!', 'Hey, hör mal zu, Kumpel! Wir haben ein großes Problem da draußen im Wasser. Diese Hammerhaie knabbern nicht mehr nur an Fischen – jetzt sind wir dran! Das sind keine gewöhnlichen Haie, oh nein. Die sind fies, zäh und doppelt so schlau. Aber hey, ein Goblin wie ich hat immer einen Plan! Ich mach’s dir schon schmackhaft, wenn du ein paar von denen erledigst. Aber, äh… geh lieber nicht allein, ja? Diese Fische haben mehr Biss, als du allein verkraften kannst. Also schnapp dir ein paar Freunde und zeig diesen Flossenfressen, wer der Boss ist!', 'Jage 3 Hammerhaie.', '', 'Kehrt zu Fizzick zwischen Sen\'jin und dem Tal der Prüfungen in Durotar zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50032 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50032, 'deDE', 'Gaur Eishorn', 'Ich würde normalerweise keine Taunka jagen, wegen ihrer engen Verbindung zur Horde... aber dieser hier ist ein Verräter und obendrein ein Krimineller. Von seinem eigenen Stamm verstoßen, paddelte er nach Azshara hinunter und wurde dabei erwischt, wie er Vorräte stahl. Gaur ist ein erfahrener Gladiator, $C. Ich wette 10 Silberstücke, dass du es nicht schaffst, ihn zu jagen und zu besiegen.', 'Besiegt Gaur Eishorn im Kampf.', '', 'Kehrt zu Spitzzahn im Scheckeneberhof in Durotar zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50033 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50033, 'deDE', 'Ghislania', 'Ich manage ein paar Gladiatoren, die ich in Wettkämpfen quer durch Kalimdor gegeneinander antreten lasse. Nehmen wir zum Beispiel Ghislania, eine Naga, die an den Küsten von Durotar entlangwandert, um ihre Fähigkeiten zu schärfen. In letzter Zeit hat sie allerdings eine Pechsträhne – drei Kämpfe in Folge verloren. Sie könnte ein wenig Motivation gebrauchen... oder vielleicht einfach nur eine ordentliche Abreibung. Geh zu ihr und fordere sie heraus. Ich wette, selbst DU könntest sie besiegen!', 'Besiegt Ghislania im Kampf.', '', 'Kehrt zu Spitzzahn im Scheckeneberhof in Durotar zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50034 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50034, 'deDE', 'Griswold Hanniston', 'Griswold hat sich in Northwatch verschanzt und tut so, als wäre er der Größte mit seinen Allianzkumpels. Er macht Ärger, schnüffelt in unserem Territorium herum und steckt seine Nase in Angelegenheiten, die ihn nichts angehen. Er hält sich für stark, aber ich habe gehört, dass er nur große Töne spuckt. Geh nach Nordwacht und mach ihn fertig. Mal sehen, ob er wirklich so stark ist, wie er vorgibt zu sein.', 'Besiegt Griswold Hanniston im Kampf.', '', 'Kehrt zu Spitzzahn im Scheckeneberhof in Durotar zurück.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50035 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50035, 'deDE', 'Hilfe benötigt', 'Ihr da, $R, nicht so schnell. Mein Boss Spitzzahn hat mich geschickt, um nach Hilfe zu suchen - und nach Vorräten. Ihr scheint kein Händler zu sein, aber wahrscheinlich könnt Ihr ihm trotzdem helfen. Er ist nördlich von hier, neben dem kaputten Wagen. Oh, und sagt ihm, ich habe Euch geschickt... Ich muss sicherstellen, dass er weiß, dass ich hier nicht nur meine Zeit vertrödel.', 'Meldet Euch bei Spitzzahn im Scheckeneberhof in Durotar.', '', 'Meldet Euch bei Spitzzahn im Scheckeneberhof in Durotar.', '', '', '', '', 16000);
DELETE FROM `quest_template_locale` WHERE `ID`=50036 AND `locale`='deDE';
INSERT INTO `quest_template_locale` (`ID`, `locale`, `Title`, `Details`, `Objectives`, `EndText`, `CompletedText`, `ObjectiveText1`, `ObjectiveText2`, `ObjectiveText3`, `ObjectiveText4`, `VerifiedBuild`) VALUES (50036, 'deDE', 'Aus Gilneas entflohen', 'Wie Ihr Euch vorstellen könnt, ist ein streunender Worg in Tirisfal ein wichtiges Ereignis. Exekutor Zygand wäre daran sicher interessiert.', 'Berichtet Exekutor Zygand in Brill in Tirisfal von der Anwesenheit des Worgen.', '', 'Berichtet Exekutor Zygand in Brill in Tirisfal von der Anwesenheit des Worgen.', '', '', '', '', 16000);
